Table_003_Alliance_40to50 = {
-----------40-41 Badlands
	[4041] = {
		title = "40-41 Badlands",
		items = {
			[1] = { str = "40-41 Badlands" },
			[2] = { str = "Go to #C_[53,43]# accept #Q_'Fiery Blaze Enchantment'#, #Q_'Mirages'#, and #Q_'A Dwarf and His Tools'#.", x = 53, y = 43, zone = "Badlands" },
			[3] = { str = "Stop at #C_[42,52]# and hand in #Q_'Martek the Exiled'# accept #Q_'Indurium'#.", x = 45, y = 52, zone = "Badlands" },
			[4] = { str = "Accept #Q_'Barbecued Buzzard Wings'#, #Q_'Pearl Diving'# (if you have them)." },
			[5] = { str = "Now there only one area for buzzards. So for the 2 quests that require their items, kill them whenever you see one." },
			[6] = { str = "Go to #C_[25,44]# and accept #Q_'Study of the Elements: Rock'# and #Q_'Coolant Heads Prevail'# then turn it back in and accept #Q_'Gyro… What?'# then turn that back in.", x = 25, y = 44, zone = "Badlands" },
			[7] = { str = "Accept #Q_'Liquid Stone'# then turn it back in." },
			[8] = { str = "Grind down to #C_[66,21]# and open the crate for #Q_'Mirages'#.", x = 66, y = 21, zone = "Badlands" },
			[9] = { str = "Grind over to the dig site at #C_[53,29]# and kill the dwarfs for #Q_'A Dwarf and His Tools'# On the right side of the hole at #C_[53,33]# touch the note in the tent for #Q_'A Sign of Hope'#.", x = 53, y = 29, zone = "Badlands" },
			[10] = { str = "Grind up to #C_[53,43]# and hand in #Q_'Mirages'# accept #Q_'Scrounging'#.", x = 53, y = 43, zone = "Badlands" },

			[11] = { str = "Also turn in #Q_'A Dwarf and His Tools'# and #Q_'A Sign of Hope'# accept the next part to Uldaman." },
			[12] = { str = "Grind down to #C_[61,54]# and accept #Q_'Tremors of the Earth'# The ogres spawn at #C_[62,70]# and wander to #C_[29,56]# so kill them if you see them. The boss is easy to pull off the back, with the small add or none.", x = 61, y = 54, zone = "Badlands" },
			[13] = { str = "Go south to #C_[62,70]# and kill ogres here for #Q_'Scrounging'# and hopefully the boss is here. There isn’t many ogres so don’t worry.", x = 62, y = 70, zone = "Badlands" },
			[14] = { str = "Grind to #C_[50,62]#, turn in #Q_'Find Agmond'# accept #Q_'Murdaloc'#.", x = 50, y = 62, zone = "Badlands" },
			[15] = { str = "Go just south to the gnoll camp and do #Q_'Murdaloc'# and #Q_'Indurium'#." },
			[16] = { str = "Go up to #C_[42,52]# and turn in #Q_'Barbecued Buzzard Wings'# and #Q_'Indurium'# accept #Q_'News for Fizzle'#.", x = 45, y = 52, zone = "Badlands" },
			[17] = { str = "Go to #C_[18,41]# and kill the lesser rock elementals for #Q_'Study of the Elements: Rock'# and #Q_'Badlands Reagent Run'#.", x = 18, y = 41, zone = "Badlands" },
			[18] = { str = "Go back to #C_[26,44]# turn in #Q_'Study of the Elements: Rock'#. Accept #Q_'Study of the Elements: Rock'#(2) and do it by killing rock elemental.", x = 26, y = 44, zone = "Badlands" },
			[19] = { str = "Go back to #C_[26,44]# turn in #Q_'Study of the Elements: Rock'#(2). Accept #Q_'Study of the Elements: Rock'#(3).", x = 26, y = 44, zone = "Badlands" },
			[20] = { str = "Kill buzzards around #C_[15,60]# and coyotes around #C_[33,62]# for #Q_'Badlands Reagent Run'# if you still need them.", x = 33, y = 62, zone = "Badlands" },

			[21] = { str = "Grind down to #C_[11,77]# and kill ogres for #Q_'Scrounging'#.", x = 11, y = 77, zone = "Badlands" },
			[22] = { str = "Kill greater rock elementals at #C_[14,88]# for #Q_'Study of the Elements: Rock'#(3).", x = 14, y = 88, zone = "Badlands" },
			[23] = { str = "Go back to #C_[26,44]# turn in #Q_'Study of the Elements: Rock'#(3). Accept #Q_'This Is Going To Be Hard'# turn around and hand it in, accept #Q_'This Is Going To Be Hard'#(2) then turn around and hand it in, accept #Q_'This Is Going To Be Hard'#(3).", x = 26, y = 44, zone = "Badlands" },
			[24] = { str = "Kill the elemental that’s summoned he’s 45. Then turn it back in to get your Nifty Stopwatch." },
			[25] = { str = "Go to #C_[53,43]# and turn in #Q_'Scrounging'#.", x = 53, y = 43, zone = "Badlands" },
			[26] = { str = "Go to #C_[61,54]# and turn in #Q_'Tremors of the Earth'# SKIP the next part.", x = 61, y = 54, zone = "Badlands" },
			[27] = { str = "Now you should be 41, if you’re not go east into the ravine and kill the whelps until you level. I usually go here and grind for at least 30 min whether or not im 41 and try to get the heart for #Q_'Fiery Blaze Enchantment'# If you don’t find it in 30 min just abandon it." },
			[28] = { str = "Use your #I_[Hearthstone]# to return to #A_Thelsamar# (#A_Loch Modan#)." },
			[29] = { str = "Go in the house behind the Inn and turn in #Q_'Badlands Reagent Run'# accept the Uldaman part." },
			[30] = { str = "Run to #C_[65,65]# turn in #Q_'Murdaloc'# accept the Uldaman part I’m almost 25% into 41 now.", x = 65, y = 65, zone = "Loch Modan" },

			[31] = { str = "Fly to #A_Darkshire# (#A_Duskwood#). Talk to the #N_Innkeeper# to make #A_Darkshire# (#A_Duskwood#) your home. Fly to #A_Stormwind# Go to #C_[64,20]# and accept #Q_'In Search of The Temple'#.", x = 64, y = 20, zone = "Stormwind" },
			[32] = { str = "Fly back to #A_Darkshire# (#A_Duskwood#), Run into #A_Swamp of Sorrows#." },
		}
	},

-----------41-41 Swamp of Sorrows
	[4141] = {
		title = "41-41 Swamp of Sorrows",
		items = {
			[1] = { str = "41-41 Swamp of Sorrows" },
