			[3] = { str = "Go to #C_[26,59]# and accept #Q_'Enroaching Wildlife'# and go do it. Kill the 3 mob types around him and north. It’s all close to the quest giver.", x = 26, y = 59, zone = "Swamp of Sorrows" },
			[4] = { str = "Go up and find Noboru the Cudgel he circles around #C_[43,38]# and drops an item that starts #Q_'Noboru the Cudgel'#.", x = 43, y = 38, zone = "Swamp of Sorrows" },
			[5] = { str = "Go back to #C_[26,59]# and turn in #Q_'Enroaching Wildlife'# accept #Q_'The Lost Caravan'#.", x = 26, y = 59, zone = "Swamp of Sorrows" },
			[6] = { str = "Grind up to #C_[25,31]# and turn in #Q_'Noboru the Cudgel'# accept #Q_'Draenethyst Crystals'#.", x = 25, y = 31, zone = "Swamp of Sorrows" },
			[7] = { str = "Go up to the sanctuary that starts at #C_[54,29]# and start collecting the #Q_'Draenethyst Crystals'#.", x = 54, y = 29, zone = "Swamp of Sorrows" },
			[8] = { str = "When you get to the last camp with the npc in the cage at #C_[65,18]# first grab the chest off the cart to the left for #Q_'The Lost Caravan'#.", x = 65, y = 18, zone = "Swamp of Sorrows" },
			[9] = { str = "Make sure you clear the camp beside the caged npc." },
			[10] = { str = "Then accept #Q_'Galen’s Escape'# it’s a breeze if you cleared the camp." },

			[11] = { str = "Grind down to #C_[48,39]# to the busted up looking tent and open the chest to complete #Q_'Galen’s Escape'#.", x = 48, y = 39, zone = "Swamp of Sorrows" },
			[12] = { str = "Go back to #C_[26,31]# and turn in #Q_'Draenethyst Crystals'#.", x = 26, y = 31, zone = "Swamp of Sorrows" },
			[13] = { str = "Go to #C_[26,59]# and turn in #Q_'The Lost Caravan'#. Accept #Q_'Driftwood'#.", x = 26, y = 59, zone = "Swamp of Sorrows" },
			[14] = { str = "Run through the swamp and go south into the Blasted Lands at #C_[36,59]# and over to Nethergarde Keep at #C_[54,78]#. Unlock the flight path by talking to the #N_Flight Master# and fly to #A_Darkshire# (#A_Duskwood#).", x = 54, y = 78, zone = "Blasted Lands" },
			[15] = { str = "Stable your cat and get the #A_Badlands# cat back out." },
			[16] = { str = "Grab #Q_'Supplies for Nethergarde'# on your way back NE." },
			[17] = { str = "Fly back to Nethergarde, turn in #Q_'Supplies for Nethergarde'# easy xp." },
			[18] = { str = "Run back up into the swamp of sorrows." },
			[19] = { str = "Run up to the lake around the Sunken Temple at #C_[70,54]# and you should get a complete message for #Q_'In Search of The Temple'#.", x = 70, y = 54, zone = "Swamp of Sorrows" },
			[20] = { str = "Go up to about #C_[76,6]# and start killing silt and monstrous crawlers and work your way south for #Q_'… and bugs'# until it’s done. Also look for wood planks on the ground for #Q_'Driftwood'#.", x = 76, y = 6, zone = "Swamp of Sorrows" },

			[21] = { str = "Use your #I_[Hearthstone]# to return to #A_Darkshire# (#A_Duskwood#) and get your cat back out." },
			[22] = { str = "Fly to Nethergarde and run back up into the swamp." },
			[23] = { str = "Go to #C_[26,59]# and turn in #Q_'Driftwood'# accept #Q_'Deliver the Shipment'#.", x = 26, y = 59, zone = "Swamp of Sorrows" },
			[24] = { str = "Run down into the Blasted Lands and turn in #Q_'Deliver the Shipment'# at #C_[66,21]#.", x = 66, y = 21, zone = "Blasted Lands" },
			[25] = { str = "Fly to #A_Booty Bay# (#A_Stranglethorn#) and turn in #Q_'Dream Dust In the Swamp'# atop the Inn and accept #Q_'Rumors for Kravel'#." },
			[26] = { str = "Get on the ship to #A_Ratchet# (#A_The Barrens#) and fly to #A_Theramore# (#A_Dustwallow Marsh#) and turn in #Q_'… and bugs'# at the Inn." },
			[27] = { str = "Fly to #A_Nijel’s Point# (#A_Desolace#)." },
		}
	},

