			[16] = { str = "Go up to the Ruins of Aboraz and kill Chucky (#C_[40,58]#) for #Q_'Voodoo Dues'# while killing the mobs for #Q_'Zanzil's Secret'# you only need 8-10.", x = 40, y = 58, zone = "Stranglethorn Vale" },
			[17] = { str = "Go up to the Ruins of Jubawal and kill Jon-Jon (#C_[34,51]#) and Maury (#C_[35,51]#) for #Q_'Voodoo Dues'# and the rest of #Q_'Zanzil's Secret'#.", x = 35, y = 51, zone = "Stranglethorn Vale" },
			[18] = { str = "Go kill Tethis for #Q_'Raptor Mastery'#(4) behind the arena around #C_[31,43]# then turn it in at nessingways at #C_[35,10]# accept #Q_'Big Game Hunter'#.", x = 31, y = 43, zone = "Stranglethorn Vale" },
			[19] = { str = "Go up to the rebel camp at #C_[38,3]# and accept #Q_'Colonel Kurzen'#.", x = 38, y = 3, zone = "Stranglethorn Vale" },
			[20] = { str = "Go to the cave at #C_[45,8]# go to the fork and go down once like earlier kill the elites and subchiefs (when they get a red aura stop your pet from hitting them or he’ll get owned) for #Q_'Colonel Kurzen'# Kurzen is at #C_[49,3]#.", x = 49, y = 3, zone = "Stranglethorn Vale" },

			[21] = { str = "Die on purpose so you’re near the rebel camp." },
			[22] = { str = "Go to the rebel camp at #C_[38,3]# and turn in #Q_'Colonel Kurzen'#.", x = 38, y = 3, zone = "Stranglethorn Vale" },
			[23] = { str = "Kill the trolls around #C_[44,34]# until you’re about 9k from leveling.", x = 44, y = 34, zone = "Stranglethorn Vale" },
			[24] = { str = "Use your #I_[Hearthstone]# to return to #A_Booty Bay# (#A_Stranglethorn#)." },
			[25] = { str = "Turn in #Q_'Zanzil's Secret'#." },
			[26] = { str = "Go down by the Bank, turn in #Q_'Akiris By the Bundle'#. Accept #Q_'Akiris By the Bundle'#(2)." },
			[27] = { str = "Go to the house in front of the half boat, turn in #Q_'Voodoo Dues'# accept #Q_'Cracking Maury's Foot'#." },
			[28] = { str = "Get on the boat to #A_Ratchet# (#A_The Barrens#) then fly to #A_Theramore# (#A_Dustwallow Marsh#)." },
			[29] = { str = "Turn in #Q_'Akiris By the Bundle'#(2) in front of you, fly to #A_Gadgetzan# (#A_Tanaris#)." },
