			[6] = { str = "Go upstairs turn in #Q_'The Missing Courier'#. Accept #Q_'The Missing Courier'#(2)." },
			[7] = { str = "Go to the gazebo at #C_[26,52]# and turn in #Q_'The Ruins of Solarsal'# accept #Q_'Return to Feathermoon Stronghold'#.", x = 26, y = 52, zone = "Feralas" },
			[8] = { str = "Go back to Feathermoon to the big building, turn in #Q_'Return to Feathermoon Stronghold'# accept #Q_'Against the Hatecrest'# turn around and hand it in accept #Q_'Against the Hatecrest'#(2)." },
			[9] = { str = "Kill naga around #C_[26,54]# for #Q_'Against the Hatecrest'#(2).", x = 26, y = 54, zone = "Feralas" },
			[10] = { str = "I kill the naga until im about 2500 TNL because I’m usually under 15k TNL when I get to this part. They give an average of 250 per kill." },

			[11] = { str = "Go back to Feathermoon to the big building, turn in #Q_'Against the Hatecrest'#(2). Accept #Q_'Against Lord Shalzaru'#." },
			[12] = { str = "Go down to the cave at #C_[26,66]# and kill Lord Shalzaru at the end for #Q_'Against Lord Shalzaru'#.", x = 26, y = 66, zone = "Feralas" },
			[13] = { str = "Die so you end up at Feathermoon." },
			[14] = { str = "Go back to Feathermoon to the big building, turn in #Q_'Against Lord Shalzaru'# accept #Q_'Delivering the Relic'# go to the other side of the building and turn it in." },
			[15] = { str = "Take the boat to the mainland." },
			[16] = { str = "Go south of the dock and kill screechers for #Q_'Screecher Spirits'#." },
			[17] = { str = "Go down to #C_[45,65]# and touch the wrecked boat to turn in #Q_'The Missing Courier'#(2). Accept #Q_'Boat Wreckage'#.", x = 45, y = 65, zone = "Feralas" },
			[18] = { str = "Use your #I_[Hearthstone]# to return to Feathermoon." },
			[19] = { str = "Go in the little building across from the Inn on the 2nd floor, turn in #Q_'Boat Wreckage'# accept #Q_'The Knife Revealed'#." },
			[20] = { str = "Go up top of the big spiral tower, turn in #Q_'The Knife Revealed'# accept #Q_'Psychometric Reading'#." },

			[21] = { str = "Go in the little building across form the Inn on the 2nd floor, turn in #Q_'Psychometric Reading'# accept #Q_'The Woodpaw Gnolls'#." },
			[22] = { str = "Take the boat to the mainland." },
			[23] = { str = "Go into the yeti cave at #C_[56,56]# and kill yeti’s for #Q_'The Mark of Quality'# you can skin them and it’s also a drop.", x = 56, y = 56, zone = "Feralas" },
			[24] = { str = "If a OOX-22/FE Distress Beacon has fallen accept the quest #Q_'Find OOX-22/FE!'# and turn it in at the back of the cave SKIP the escort." },
			[25] = { str = "Grind east and kill the Brutes around #C_[60,56]#, and warlocks and shamans around #C_[59,66]# for #Q_'The High Wilderness'# I kill any I see.", x = 60, y = 56, zone = "Feralas" },
			[26] = { str = "Go up to #C_[65,45]# and accept #Q_'Freedom For All Creatures'# It’s pretty hidden, just NW of the camp entrance is a path that doesn’t even look like you can walk on it. To do this simply escort her to the cage, open it and kill anything until it says complete.", x = 65, y = 45, zone = "Feralas" },
			[27] = { str = "Hand it back in and accept #Q_'Doling Justice'# The mobs can be found in this camp." },
			[28] = { str = "Turn #Q_'Doling Justice'# in at #C_[65,45]# and accept #Q_'Doling Justice'#(2).", x = 65, y = 45, zone = "Feralas" },
			[29] = { str = "Go down to #C_[73,56]# and touch Large Leather Backpacks on the big tree. Turn in #Q_'The Woodpaw Gnolls'# accept #Q_'The Writhing Deep'#.", x = 73, y = 56, zone = "Feralas" },
			[30] = { str = "Right click the Backpack, accept #Q_'Thalanaar Delivery'#." },

			[31] = { str = "Grind down to the hive at #C_[73,63]# and go all the way down. Go into the southern tunnel and stay right, You’ll see a Zukk’Ash Pod there. Turn in #Q_'The Writhing Deep'# accept #Q_'Freed From the Hive'#.", x = 73, y = 63, zone = "Feralas" },
			[32] = { str = "Use your #I_[Hearthstone]# to return to Feathermoon." },
			[33] = { str = "On the water side of the Gazebo, turn in #Q_'The Mark of Quality'# accept #Q_'Improved Quality'#." },
			[34] = { str = "Go in the small house across from the Inn, turn in #Q_'The High Wilderness'# then go upstairs and turn in #Q_'Freed From the Hive'# accept #Q_'A Hero’s Welcome'#." },
			[35] = { str = "Go to the big building next door and turn in #Q_'A Hero’s Welcome'#. Accept #Q_'Rise of the Silithid'#." },
			[36] = { str = "Fly out to #A_Thalanaar# (#A_Feralas#) (spot between #A_Thousand Needles# and here) turn in #Q_'Thalanaar Delivery'# at #C_[89,46]#.", x = 89, y = 46, zone = "Feralas" },
			[37] = { str = "Fly to #A_Darnassus#." },
			[38] = { str = "Follow the dock and go in the house, turn in #Q_'Handle With Care'# accept #Q_'Favored of Elune?'#." },
			[39] = { str = "Go upstairs, turn in #Q_'In Search of Knowledge'# then go to the back of the house on the 2nd floor, open the green book and accept #Q_'Feralas: A History'# turn it back in to her, accept #Q_'The Borrower'#." },
			[40] = { str = "Talk to your #N_Class Trainer# to learn new spells and abilities." },

			[41] = { str = "Go to the temple of the moon at #C_[39,86]# and up to the 2nd floor, turn in #Q_'Rise of the Silithid'# and #Q_'Doling Justice'#(2).", x = 39, y = 86, zone = "Feralas" },
			[42] = { str = "Buy 2 Elixir of Fortitude for a later part of this quest." },
			[43] = { str = "Use your #I_[Hearthstone]# to return to Feathermoon and fly to #A_Gadgetzan# (#A_Tanaris#) and turn in #Q_'The Borrower'# accept #Q_'The Super Snapper FX'#." },
		}
	},

-----------45-46 Uldaman
	[4546] = {
		title = "45-46 Uldaman",
		items = {
			[1] = { str = "45-46 Uldaman" },
			[2] = { str = "Now you can either grind on the gnolls in Feralas around where you found the backpack on the tree, or you can do Uldaman. I highly suggest you do Uldaman. You picked up some quests earlier, there are a few more in #A_Ironforge# and in the #A_Badlands#. If you get a good group build you can easily do just about every quest in there in under 3 hours max. I usually fully do Uldaman inside and out, I don’t finish all the quests unless I have to level. 1 full run inside and out should get you 75-100% through this level. I usually do this in about 3 hours no problem. Plus you can solo all the mobs outside of the instance 3 at a time no problem. The end of Uldaman, after the boss, (mini disks quest) gives about 20k xp between that and talking to the ghostly npc who spawns in there, as well as gives some nice xp for quests later on. And you could miss some xp spots for later quests I do if you skip this." },
			[3] = { str = "I don’t list all the quests for Uldaman because there is a good handful and you don’t have to do them all to fully level, and again it is optional. This is much faster than grinding would be. I really hate to do instances when trying for the best time but alliance has less quests than the horde do and I did mention there will be about 10-15% of grinding on your way to 60. If you did do all instances though you would be really slowed down. This isn’t going to put a break in your time with a good group, preferrably guild groups of 45+." },
			[4] = { str = "You can stop at about 75% to 46 but again I suggest you level. It’s not as bad as it seems." },
			[5] = { str = "A great map of Uldaman and where the parts of most of the quests pieces can be done at is listed here on worldofwar.net." },
			[6] = { str = "Once you’re all set, fly to #A_Stormwind# and turn in #Q_'In Search of The Temple'# at #C_[64,20]# and accept #Q_'In Search of The Hinterlands'#. Talk to your #N_Class Trainer# to learn new spells and abilities. Then fly to #A_Southshore# (#A_Hillsbrad Foothills#).", x = 64, y = 20, zone = "Stormwind" },
			[7] = { str = "Run up to the path at #C_[84,33]# behind Durnholde Keep (you’ll see 2 griffins) into The Hinterlands.", x = 84, y = 33, zone = "Tarren Mill" },
		}
	},

-----------46-47 The Hinterlands
	[4647] = {
		title = "46-47 The Hinterlands",
		items = {
			[1] = { str = "46-47 The Hinterlands" },
			[2] = { str = "Go up to #C_[11,46]# and turn in #Q_'In Search of The Hinterlands'# accept #Q_'Gryphon Master Talonaxe'#.", x = 11, y = 46, zone = "The Hinterlands" },
			[3] = { str = "Unlock the flight path by talking to the #N_Flight Master# at #C_[11,46]# way up top then go in the cave and turn in #Q_'Gryphon Master Talonaxe'#, accept #Q_'Rhapsody Shindigger'# and #Q_'Witherbark Cages'#.", x = 11, y = 46, zone = "The Hinterlands" },
			[4] = { str = "Go into the Inn and turn right, accept #Q_'Skulk Rock Clean-Up'# and #Q_'Troll Necklace Bounty'#." },
			[5] = { str = "Go way in the back of the Inn up to around #C_[13,41]#. Talk to the #N_Innkeeper# to make #A_Aerie Peak# (#A_The Hinterlands#) your home.", x = 13, y = 41, zone = "The Hinterlands" },
			[6] = { str = "Grind east to #C_[20,48]# to the path while keeping an eye out for feathers on the ground for #Q_'Favored of Elune?'# You don’t need them all yet.", x = 20, y = 48, zone = "The Hinterlands" },
			[7] = { str = "Follow the path up to #C_[26,48]# and turn in #Q_'Rhapsody Shindigger'# accept #Q_'Rhapsody's Kalimdor Kocktail'#.", x = 26, y = 48, zone = "The Hinterlands" },
			[8] = { str = "Go back down and to the troll camp and clear most of them out at #C_[23,58]# and open the 2 cages for #Q_'Witherbark Cages'#.", x = 23, y = 58, zone = "The Hinterlands" },
			[9] = { str = "Grind east to #C_[31,57]# and open the 3rd cage for #Q_'Witherbark Cages'#.", x = 31, y = 57, zone = "The Hinterlands" },
			[10] = { str = "You should also have 5 necklaces for #Q_'Troll Necklace Bounty'# now." },

			[11] = { str = "Go NE up to #C_[46,42]#, collecting feathers if you still need them on the way, and kill the Green Sludges for #Q_'Skulk Rock Clean-Up'#.", x = 46, y = 62, zone = "The Hinterlands" },
			[12] = { str = "Go to #C_[49,37]# if you found OOX-09/HL Distress Beacon.", x = 49, y = 37, zone = "The Hinterlands" },
			[13] = { str = "Go to #C_[56,41]# and kill Jade Oozes for of #Q_'Skulk Rock Clean-Up'#(2).", x = 56, y = 41, zone = "The Hinterlands" },
			[14] = { str = "Go to the waterfall at #C_[81,46]# and dive into the water below, then go south along this shore until you see Gammerita and take her picture for #Q_'The Super Snapper FX'# Also you want to keep an eye out for bottles along the shore for #Q_'Whiskey Slim's Lost Grog'#.", x = 81, y = 46, zone = "The Hinterlands" },
			[15] = { str = "Use your #I_[Hearthstone]# to return to #A_Aerie Peak# (#A_The Hinterlands#)." },
			[16] = { str = "Go to the front of the Inn and turn in #Q_'Skulk Rock Clean-Up'#(2) and #Q_'Troll Necklace Bounty'# and any extra 5 necklaces you have." },
			[17] = { str = "Go up past the flight path in the cave at #C_[9,44]# and turn in #Q_'Witherbark Cages'# accept #Q_'The Altar of Zul'#.", x = 9, y = 44, zone = "The Hinterlands" },
			[18] = { str = "Go run up top of the Altar of Zul at #C_[48,68]# for #Q_'The Altar of Zul'#.", x = 48, y = 68, zone = "The Hinterlands" },
			[19] = { str = "Now I grind on the trolls and wolves around Zul until I’m 1 bar from 47. You should be at least half way through 46 at this point." },
			[20] = { str = "Get the quest #Q_'Jammal'an the Prophet'# at #C_[33,75]# if you plan to do Sunken Temple later.", x = 33, y = 75, zone = "The Hinterlands" },

			[21] = { str = "I’m sure by the time you near 47 you’re going to find OOX-09/HL Distress Beacon accept #Q_'Find OOX-09/HL'# and turn it in at #C_[49,37]# and then accept #Q_'Rescue OOX-09/HL'# and escort it to #C_[78,61]# Theres only 2 encounters of 3 mobs, but watch out for the stealthed wolves.", x = 49, y = 37, zone = "The Hinterlands" },
			[22] = { str = "Once you’re about 6k or 1 bar from 47 use your #I_[Hearthstone]# to return to #A_Aerie Peak# (#A_The Hinterlands#)." },
			[23] = { str = "Turn in any #Q_'Troll Necklace Bounty'# you have just before you go out." },
			[24] = { str = "Go up past flight path to #C_[9,44]# and turn in #Q_'The Altar of Zul'# accept #Q_'Thadius Grimshade'#.", x = 9, y = 44, zone = "The Hinterlands" },
			[25] = { str = "Fly to #A_Menethil Harbor# (#A_The Wetlands#) and take the boat to #A_Auberdine# (#A_Darkshore#), then fly to Rut’theran Village." },
			[26] = { str = "Run to the house at #C_[55,92]# and turn in #Q_'Favored of Elune?'#.", x = 55, y = 92, zone = "The Hinterlands" },
			[27] = { str = "Go upstairs, turn in #Q_'The Super Snapper FX'# accept #Q_'Return to Troyas'#." },
			[28] = { str = "You should now be 47 and about 8k into it." },
			[29] = { str = "Fly to #A_Feathermoon Stronghold# (#A_Feralas#)." },
		}
	},

-----------47-47 Feralas
	[4747] = {
		title = "47-47 Feralas",
		items = {
			[1] = { str = "47-47 Feralas" },
			[2] = { str = "Talk to the #N_Innkeeper# to make #A_Feathermoon Stronghold# (#A_Feralas#) your home." },
			[3] = { str = "Go across from the Inn to the 2 story house, turn in #Q_'Return to Troyas'# accept #Q_'The Stave of Equinex'#." },
			[4] = { str = "Accept #Q_'The Sunken Temple'# Next to him." },
			[5] = { str = "Swim or boat east to the dock." },
			[6] = { str = "Beside the dock at #C_[44,33]# accept #Q_'Zapped Giants'# and #Q_'Fuel for Zapping'#.", x = 44, y = 33, zone = "Feralas" },
			[7] = { str = "Make the Zorbin's Ultra-Shrinker a hotkey. Then go south along the shore and start zapping giants and killing them for #Q_'Zapped Giants'# and kill water elementals for #Q_'Fuel for Zapping'#." },
			[8] = { str = "Go back to #C_[44,33]# and turn in #Q_'Zapped Giants'# and #Q_'Fuel for Zapping'# You can now do those again, but I wouldn’t, for about 500 xp per turn in. Now he opens his store to you.", x = 44, y = 33, zone = "Feralas" },
			[9] = { str = "Run over to the grimtotem camp at #C_[65,45]# and accept #Q_'An Orphan Looking For A Home'#.", x = 65, y = 45, zone = "Feralas" },
			[10] = { str = "Go down to the yeti cave at #C_[56,56]# and do the robot chicken escort if you can, don’t be afraid to ask for help if needed. It’s really easy now.", x = 56, y = 56, zone = "Feralas" },

			[11] = { str = "Grind to the yeti cave at #C_[51,31]# on Ironfur Bears and Groddoc Gorillas until you get 3 livers from each of them for #Q_'Rhapsody's Kalimdor Kocktail'#.", x = 51, y = 31, zone = "Feralas" },
			[12] = { str = "Kill the Rage Scar Yeti’s here for #Q_'Improved Quality'# remember you can skin them for these as well. If a Pristine Yeti Hide drops, accept the quest it gives #Q_'Pristine Yeti Hide'#." },
			[13] = { str = "Go to Rockbiter at #C_[42,22]# and accept #Q_'The Giant Guardian'#.", x = 42, y = 22, zone = "Feralas" },
			[14] = { str = "Do #Q_'The Stave of Equinex'# by doing the following. Get the Blytan Essence from the fire at #C_[38,15]# #Q_'The Stave of Equinex'# Get the Samha Essence from the fire at #C_[40,12]# (atop the ruined arch) to get to it, go around back and hop up the broken rocks (it’s not easily noticed) Go back out the back and Grab the Imbel Essence from the fire at #C_[39,9]#. Get the Lahassa Essence from the fire at #C_[37,12]# under the brown gazebo. Go down to the Equinex Monolith just over the cliff. Use Troya’s Staff next to it. Touch the Monolith and turn in #Q_'The Stave of Equinex'# accept #Q_'The Morrow Stone'#.", x = 38, y = 15, zone = "Feralas" },
			[15] = { str = "Go back up near the previous flame to #C_[38,10]# and turn in #Q_'The Giant Guardian'# accept the escort #Q_'Wandering Shay'#.", x = 38, y = 10, zone = "Feralas" },
			[16] = { str = "Grab the bell from the chest and hotkey it. This is similar to the druid quest back in darkshore. Run her back to Rockbiter at #C_[42,21]#and turn in #Q_'Wandering Shay'# (the trick is to keep ahead of her, if she’s with you by your side she will wander off a lot more and the bell has a 1 minute timer.", x = 42, y = 21, zone = "Feralas" },
			[17] = { str = "Use your #I_[Hearthstone]# to return to Feathermoon Stronghold." },
			[18] = { str = "Go to the gazebo near the water beside the hippograph master, turn in #Q_'Pristine Yeti Hide'# and #Q_'Improved Quality'#." },
			[19] = { str = "Take your 2 Elixirs of Fortitude out of your mailbox if you don’t already have them on you." },
			[20] = { str = "Go across to the 2 story house, turn in #Q_'The Morrow Stone'#." },

			[21] = { str = "You should be close to 70% into 47." },
			[22] = { str = "Fly to #A_Gadgetzan# (#A_Tanaris#)." },
		}
	},

-----------47-48 Tanaris
	[4748] = {
		title = "47-48 Tanaris",
		items = {
			[1] = { str = "47-48 Tanaris" },
			[2] = { str = "Just inside #A_Gadgetzan# (#A_Tanaris#) to the right, accept #Q_'The Thirsty Goblin'#." },
			[3] = { str = "Talk to the #N_Innkeeper# to make #A_Gadgetzan# (#A_Tanaris#) your home." },
			[4] = { str = "Go behind the Inn, accept #Q_'The Dunemaul Compound'#." },
			[5] = { str = "Near the north entrance, accept #Q_'Thistleshrub Valley'#." },
			[6] = { str = "Go south to the skeleton in the ground at #C_[49,36]# and #C_[44,39]# and #C_[47,45]# kill Rocs for #Q_'Rhapsody's Kalimdor Kocktail'# If it’s busy just do it later.", x = 49, y = 36, zone = "Tanaris" },
			[7] = { str = "Ride over to #C_[52,45]# turn in #Q_'The Sunken Temple'# and accept #Q_'The Stone Circle'# and #Q_'Gahz'Ridian'#.", x = 52, y = 45, zone = "Tanaris" },
			[8] = { str = "Now put on the Gahz’Ridian Detector Helm and yellow dots on mini map will show the pieces you need to get for #Q_'Gahz'Ridian'#. It’s up to you if you want to wear this in fights. This stuff is all over." },
			[9] = { str = "Run over west to the Dunemaul Compound clear a path to #C_[40,58]# and kill Gor’Marok the Ravager for #Q_'The Dunemaul Compound'# You don’t need the 20 ogre kills all from here.", x = 40, y = 58, zone = "Tanaris" },
			[10] = { str = "Head over to #C_[47,65]# and finish killing the ogres for #Q_'The Dunemaul Compound'# while gathering the rest of the artifacts for #Q_'Gahz'Ridian'#.", x = 47, y = 65, zone = "Tanaris" },

			[11] = { str = "Grind on the ogres until you’re 10.000 XP from 48." },
			[12] = { str = "Sneak up the left side of uldum and go up to the pedestal at #C_[37,81]# and turn in #Q_'Seeing What Happens'#.", x = 37, y = 81, zone = "Tanaris" },
			[13] = { str = "Talk to the guy who appears, accept #Q_'The Stone Watcher'# and talk to him until completed. Then touch the pedastal to turn it back in." },
			[14] = { str = "Touch the pedastal one more time and accept #Q_'Return to Ironforge'#." },
			[15] = { str = "Go west to #C_[29,66]# and kill Gnarled Thistlegrub and Thistlegrub Rootshapers for #Q_'Thistleshrub Valley'# and the dew collectors for #Q_'The Thirsty Goblin'# You should hit 48 here.", x = 29, y = 66, zone = "Tanaris" },
			[16] = { str = "If you find Tooga, accept #Q_'Tooga’s Quest'# I found him at #C_[29,73]# near the zepplin, but you can find him anywhere around this valley. He follows you, you don’t have to follow him.", x = 29, y = 73, zone = "Tanaris" },
			[17] = { str = "Go up to the noxious lair around #C_[34,46]# and kill centipaar there for #Q_'Noxious Lair Investigation'# If you can’t get them all before tooga has 10 minutes left, go turn that in and then come back.", x = 34, y = 76, zone = "Tanaris" },
			[18] = { str = "Turn in #Q_'Tooga’s Quest'# near steamwheedle port at #C_[66,25]#.", x = 66, y = 25, zone = "Tanaris" },
			[19] = { str = "Accept #Q_'Yuka Screwspigot'#." },
			[20] = { str = "Turn in #Q_'Screecher Spirits'# at #C_[66,22]# SKIP the ZF part.", x = 66, y = 22, zone = "Tanaris" },

			[21] = { str = "Use your #I_[Hearthstone]# to return to #A_Gadgetzan# (#A_Tanaris#)." },
			[22] = { str = "Go near the entrance and accept #Q_'The Thirsty Goblin'# back and turn it in accept #Q_'In Good Taste'#." },
			[23] = { str = "Go behind the Inn near the fire, turn in #Q_'The Dunemaul Compound'#." },
			[24] = { str = "Go near the north entrance, hand in #Q_'Thistleshrub Valley'#." },
			[25] = { str = "Go west to Sprinkle, turn in #Q_'In Good Taste'# accept #Q_'Sprinkle's Secret Ingredient'#." },
			[26] = { str = "In the building behind her, turn in #Q_'Noxious Lair Investigation'#." },
			[27] = { str = "Go on the hill behind the building, accept #Q_'The Schrimshank Redemption'#." },
			[28] = { str = "Run down to #C_[52,45]# and turn in #Q_'Gahz'Ridian'#.", x = 52, y = 45, zone = "Tanaris" },
			[29] = { str = "Run down to the entrance at #C_[55,70]# take the spiral down to the entrance that is north. first fork go right. After the circular room at the next fork go right. You will enter a big square room. Go right to #C_[55,71]# and grab the survey gear for #Q_'The Schrimshank Redemption'#.", x = 55, y = 70, zone = "Tanaris" },
			[30] = { str = "Die on purpose so you end up at #A_Gadgetzan# (#A_Tanaris#)." },

			[31] = { str = "Go to the hill on the west side of town, turn in #Q_'The Schrimshank Redemption'# accept #Q_'Insect Part Analysis'#." },
			[32] = { str = "Go to the house on the NW part of town, turn in #Q_'Insect Part Analysis'# accept #Q_'Insect Part Analysis'#(2)." },
			[33] = { str = "Go back up on the hill, turn in #Q_'Insect Part Analysis'#(2) accept #Q_'Rise of the Silithid'#." },
			[34] = { str = "Run up to the Shimmering Flats to #C_[78,74]# and turn in #Q_'An Orphan Looking For A Home'# accept #Q_'A Short Incubation'# and since you already have this, turn it back in and accept #Q_'The Newest Member of the Family'# now you have 1 hour.", x = 78, y = 74, zone = "Thousand Needles" },
			[35] = { str = "Fly to #A_Theramore# (#A_Dustwallow Marsh#) and get any mageweave you have out and build up first aid as much as you can." },
			[36] = { str = "Boat to #A_Menethil Harbor# (#A_The Wetlands#) and fly to #A_Ironforge#." },
			[37] = { str = "Talk to the #N_Innkeeper# to make #A_Ironforge# your home. Talk to your #N_Class Trainer# to learn new spells and abilities." },
			[38] = { str = "Go to #C_[77,11]# and turn in #Q_'Return to Ironforge'# accept #Q_'A Future Task'# and #Q_'Passing the Burden'#.", x = 77, y = 11, zone = "Ironforge" },
			[39] = { str = "Turn around to #C_[70,18]# and turn in #Q_'A Future Task'#.", x = 70, y = 18, zone = "Ironforge" },
			[40] = { str = "Go to #C_[31,4]# and turn in #Q_'Passing the Burden'# accept #Q_'Arcane Runes'# and #Q_'An Easy Pickup'#.", x = 31, y = 4, zone = "Ironforge" },

			[41] = { str = "Go to #C_[70,93]#, turn in #Q_'An Easy Pickup'# accept #Q_'Signal For Pickup'# then turn it back in. Now you got the flare gun to signal later on.", x = 70, y = 93, zone = "Ironforge" },
			[42] = { str = "Fly to #A_Aerie Peak# (#A_The Hinterlands#)." },
		}
	},

-----------48-48 The Hinterlands
	[4848] = {
		title = "48-48 The Hinterlands",
		items = {
			[1] = { str = "48-48 The Hinterlands" },
			[2] = { str = "Go in the base downstairs to #C_[14,43]# and turn in #Q_'The Newest Member of the Family'# accept #Q_'Food For Baby'#.", x = 14, y = 43, zone = "The Hinterlands" },
			[3] = { str = "Run up to #C_[26,48]# and turn in #Q_'Rhapsody's Kalimdor Kocktail'# Accept #Q_'Rhapsody's Tale'#.", x = 26, y = 48, zone = "The Hinterlands" },
			[4] = { str = "Grind down to the lake at #C_[40,59]# and Violet Tragan (Mushroom) from the bottom of the lake for #Q_'Sprinkle's Secret Ingredient'#.", x = 40, y = 59, zone = "The Hinterlands" },
			[5] = { str = "Grind until you’re east of #C_[57,47]# and kill Silvermane Stalkers for #Q_'Food For Baby'#.", x = 57, y = 47, zone = "The Hinterlands" },
			[6] = { str = "Run back to #A_Aerie Peak# (#A_The Hinterlands#) and go to the bottom floor of the Inn." },
			[7] = { str = "Turn in #Q_'Food For Baby'# accept #Q_'Becoming A Parent'# then turn it back in. Cute pet and good xp =)." },
			[8] = { str = "Use your #I_[Hearthstone]# to return to #A_Ironforge# and fly to #A_Booty Bay# (#A_Stranglethorn#)." },
		}
	},

-----------48-49 Stranglethorn Vale
	[4849] = {
		title = "48-49 Stranglethorn Vale",
		items = {
			[1] = { str = "48-49 Stranglethorn Vale" },
			[2] = { str = "Talk to the #N_Innkeeper# to make #A_Booty Bay# (#A_Stranglethorn#) your home." },
			[3] = { str = "Turn in #Q_'Back To Booty Bay'# and #Q_'Whiskey Slim's Lost Grog'#." },
			[4] = { str = "Enter the house next to the upside down, half ship, turn in #Q_'Deliver to MacKinley'#." },
			[5] = { str = "Under that boat outside, accept #Q_'Stranglethorn Fever'#." },
			[6] = { str = "Go up the ramp and first house past the bridge, turn in all of the robot chicken escorts you’ve gotten. I didn’t get the #A_Tanaris# one while writing this guide so I missed out on the quest to get 7100 extra exp." },
			[7] = { str = "Near the boat, accept #Q_'The Captain's Chest'#." },
			[8] = { str = "Go kill gorillas around #C_[34,63]# for #Q_'Stranglethorn Fever'# then go up into the cave at #C_[35,60]# turn it in. the guy in the cave asks for 10 gorilla fangs, then he summons 3 waves of gorillas. Mokk is in the 3rd wave. They’re all around level 42-44 it starts with 3, then 5 then I think 6 or 7. It can get tough. Just kill Mokk first in case you die.", x = 35, y = 60, zone = "Stranglethorn Vale" },
			[9] = { str = "Go up to #C_[38,34]# and kill King Bangalash for #Q_'Big Game Hunter'# he spawns level 33 minions. You can kill him before a few come out. Not like they’re a problem.", x = 38, y = 34, zone = "Stranglethorn Vale" },
			[10] = { str = "Run up to #C_[48,25]# and kill the elite ogres all around here until Maury’s Key drops for #Q_'Cracking Maury’s Foot'# If you don’t find this after an hour just abandon the quest. It has a low drop rate. I grind till im 2 bars from 49 if I don’t get the key. Usally it’s about an hours worth.", x = 48, y = 25, zone = "Stranglethorn Vale" },

			[11] = { str = "Go up to Nessingways at #C_[35,10]# and turn in #Q_'Big Game Hunter'#.", x = 35, y = 10, zone = "Stranglethorn Vale" },
			[12] = { str = "Use your #I_[Hearthstone]# to return to #A_Booty Bay# (#A_Stranglethorn#) Run up to the half boat just outside the Inn." },
			[13] = { str = "Turn in #Q_'Stranglethorn Fever'#." },
			[14] = { str = "Run to the shore just east of #A_Booty Bay# (#A_Stranglethorn#) and open blue bottles until you get Carefully Folded Note which starts #Q_'Message in A Bottle'#." },
			[15] = { str = "Go to #C_[36,69]# and kill Gorlash for #Q_'The Captain's Chest'# He’s a 47 elite, it can be tough but I did it. Use everything you have.", x = 36, y = 69, zone = "Stranglethorn Vale" },
			[16] = { str = "Go to #C_[38,80]# and turn in #Q_'Message in A Bottle'# SKIP the next part, unless you can get a group to kill the 51 ape.", x = 38, y = 80, zone = "Stranglethorn Vale" },
			[17] = { str = "Go down to the pirate ships, 1 is at #C_[33,87]# and the other 2 are at #C_[30,88]# You want to go to the 2nd floor near the front of the ship and kill the captain of each for #Q_'The Bloodsail Buccaneers'#(5).", x = 33, y = 87, zone = "Stranglethorn Vale" },
			[18] = { str = "Look for a scroll on one of the ships called Cortello's Riddle which starts #Q_'Cortello's Riddle'#." },
			[19] = { str = "If your #I_[Hearthstone]# is up, use it, otherwise die so you end up at #A_Booty Bay# (#A_Stranglethorn#)." },
			[20] = { str = "Go near the boat, turn in #Q_'The Captain's Chest'#." },

			[21] = { str = "Go up top of the Inn, turn in #Q_'The Bloodsail Buccaneers'#(5)." },
			[22] = { str = "Fly to #A_Stormwind# and go to #C_[64,20]# turn in #Q_'Rhapsody's Tale'# SKIP the rest.", x = 64, y = 20, zone = "Stormwind" },
			[23] = { str = "From here on, either look at the AH when you go by one, or use your AH char to search for your Beaststalker Bracers. I got mine for 8g also look for a mithril casing for later in Un’Goro." },
			[24] = { str = "Fly to #A_Darkshire# (#A_Duskwood#)." },
			[25] = { str = "Run east through #A_Deadwind Pass# to the #A_Swamp of Sorrows#." },
			[26] = { str = "Follow the road until it forks and the left fork has a bridge at #C_[22,48]# and under it is A Soggy Scroll which turns in #Q_'Cortello's Riddle'#. Accept #Q_'Cortello's Riddle'#(2).", x = 22, y = 48, zone = "Swamp of Sorrow" },
			[27] = { str = "Run down to #A_The Blasted Lands#." },
		}
	},

--------49-50 Blasted Lands
	[4950] = {
		title = "49-50 Blasted Lands",
		items = {
			[1] = { str = "49-50 Blasted Lands" },
			[2] = { str = "No need to rush through this zone, so grind on anything you see." },
			[3] = { str = "Stop at #C_[50,14]# and accept the following 5 quests: #Q_'The Basilisk's Bite'# #Q_'Vulture's Vigor'# #Q_'Snickerfang Jowls'# #Q_'A Boar's Vitality'# and #Q_'The Decisive Striker'#.", x = 50, y = 14, zone = "Blasted Lands" },
			[4] = { str = "You’re going to need the following amount of items to complete all 5 of these quests. Snickerfang Jowls:5, Blasted Boar Lungs:6, Scorpok Pincers:6, Basilisk Brain:11, Vulture Gizzards:14 It’s going to be like grinding getting all this so you’re killing 2 birds with 1 stone. You’ll most likely gain half a level before you turn these in." },
			[5] = { str = "Just circle the center of the map and you’ll find all the stuff you need to kill. I’d mark it with coords but there would be way too many. You might want to avoid anything 53 and up which are farther to the south You should make some nice money with skinning here." },
			[6] = { str = "You should be close to 3 ½ bars into 49 now." },
			[7] = { str = "Grind down to #C_[51,35]# and accept #Q_'Everything Counts In Large Amounts'# If you do find one of these, hand it back in when you pass her, you’re bound to find at least one while here. Don’t even bother with the other quest you’ll Never find it. I’ve been playing WoW since release and never found one.", x = 51, y = 35, zone = "Blasted Lands" },
			[8] = { str = "Go to #C_[50,14]# and turn in the 5 quests, #Q_'The Basilisk's Bite'# #Q_'Vulture's Vigor'# #Q_'Snickerfang Jowls'# #Q_'A Boar's Vitality'# and #Q_'The Decisive Striker'# but turn in the one you want the most last, because you get buffed and get an item as well. These each give 4700 xp.", x = 50, y = 14, zone = "Blasted Lands" },
			[9] = { str = "Go to Nethergarde Keep to the top of the tall tower at #C_[66,19]# and turn in #Q_'Thadius Grimshade'# SKIP the next part.", x = 66, y = 19, zone = "Blasted Lands" },
			[10] = { str = "You should be close to 70% into 49 now. If you don’t want to do ZF to hit 50, grind here until you’re about 13k from 50. If you wanna do ZF go to the next step." },

			[11] = { str = "Use your #I_[Hearthstone]# to return to #A_Booty Bay# (#A_Stranglethorn#)." },
			[12] = { str = "Accept #Q_'Zanzil's Mixture'# and #Q_'A Fool's Stout'#." },
			[13] = { str = "Get on the boat to #A_Ratchet# (#A_The Barrens#)." },
			[14] = { str = "Accept #Q_'Volcanic Activity'# at the first hut off the dock." },
			[15] = { str = "Just outside this hut is a green chest, open it for #Q_'The Stone Circle'#." },
			[16] = { str = "Fly to #A_Gadgetzan# (#A_Tanaris#)." },
			[17] = { str = "Go near the NW entrance, turn in #Q_'Sprinkle's Secret Ingredient'# accept #Q_'Delivery For Marin'#." },
			[18] = { str = "Go back near the South entrance and turn in #Q_'Delivery For Marin'# accept #Q_'Noggenfogger Elixir'# then turn it back in. Now you get 5 elixirs and can buy them. These have some useful effects but are more fun than useful." },
			[19] = { str = "Run in the Shimmering Flats to #C_[77,77]# and turn in #Q_'Zanzil's Mixture'# and #Q_'A Fool's Stout'# accept #Q_'Get the Goblins Drunk'# go to #C_[79,76]# and turn it in.", x = 77, y = 77, zone = "Thousand Needles" },
			[20] = { str = "Go back to #C_[77,77]# and accept #Q_'Report Back to Fizzlebub'#.", x = 77, y = 77, zone = "Thousand Needles" },

			[21] = { str = "Go to #C_[52,45]# and turn in #Q_'The Stone Circle'# SKIP the rest unless you plan on doing the Sunken Temple.", x = 52, y = 45, zone = "Thousand Needles" },
			[22] = { str = "You should be about 30k from 50 now, Like I said above, either go do ZF or you should have grinded in Blasted Lands." },
			[23] = { str = "If you do ZF you only really need to complete at least 2-3 of the following quests #Q_'Troll Temper'# #Q_'Divino-matic Rod'# #Q_'Gahz'rilla'# #Q_'The Prophecy of Mosh'Aru'# I highly suggest getting #Q_'Divino-matic Rod'# and #Q_'Gahz'rilla'# because you get an awesome ring and a mount speed inscrease trinket which I’ve used since the start of WoW and you will use them on up to 60 and then some." },
			[24] = { str = "Now you should be about 1-2 bars into 50 if you went to ZF. From here on out there is a lot more fresh content, as you seen 45-50 is a pain in the ass. We still have a lot of untouched areas to XP in now." },
			[25] = { str = "Use your #I_[Hearthstone]# to return to #A_Booty Bay# (#A_Stranglethorn#) and turn in #Q_'Report Back to Fizzlebub'#." },
			[26] = { str = "Fly to #A_Ironforge# and talk to your #N_Class Trainer# to learn new spells and abilities. Accept the quest #Q_'The Hunter's Charm'#." },
			[27] = { str = "Make sure you get at least 15 silk cloths out of the mailbox or AH alt." },
			[28] = { str = "Talk to the #N_Innkeeper# to make #A_Ironforge# your home. Accept #Q_'Assisting Arch Druid Staghelm'#." },
			[29] = { str = "Fly to #A_Thelsamar# (#A_Loch Modan#)." },
			[30] = { str = "Run down into the #A_Badlands# all the way west to #C_[1,62]# and enter the #A_Searing Gorge#.", x = 1, y = 62, zone = "Badlands" },
		}
	},
}
