Table_003_Alliance_50to60 = {
-----------50-51 Searing Gorge
	[5051] = {
		title = "50-51 Searing Gorge",
		items = {
			[1] = { str = "50-51 Searing Gorge" },
			[2] = { str = "Go down to #C_[65,62]# and touch the outhouse, accept #Q_'Caught!'#.", x = 65, y = 62, zone = "Searing Gorge" },
			[3] = { str = "Go just below the outhouse and kill the geologists for #Q_'Caught!'#." },
			[4] = { str = "Clear all the mobs and at the bottom of the little pit, accept #Q_'Suntara Stones'# escort him to #C_[74,19]# and when he dies grab the note which starts #Q_'Suntara Stones'#(2).", x = 74, y = 19, zone = "Searing Gorge" },
			[5] = { str = "Run back down near the outhouse at #C_[65,62]# and get the last of your geologist kills if you need them and turn in #Q_'Caught!'# at the outhouse, accept #Q_'Ledger From Tanaris'# and grab the book he slips you under the door.", x = 65, y = 62, zone = "Searing Gorge" },
			[6] = { str = "Run down to #C_[71,72]# and kill Margol the Rager, and grab Margol's Horn off him which starts #Q_'The Horn of the Beast'#. This gives you the searing gorge key, useless now that there is a flight path here but good XP.", x = 71, y = 72, zone = "Searing Gorge" },
			[7] = { str = "Run over to #C_[39,38]# and accept #Q_'Divine Retribution'# then talk to him until completed, accept #Q_'The Flawless Flame'#.", x = 39, y = 38, zone = "Searing Gorge" },
			[8] = { str = "Run up to #A_Thorium Point# (#A_Searing Gorge#) at #C_[38,26]#.", x = 38, y = 26, zone = "Searing Gorge" },
			[9] = { str = "Grab these quests off the sign: #Q_'WANTED: Overseer Maltorius'# (this will require a group) #Q_'STOLEN: Smithing Tuyere'# and #Q_'Lookout's Spyglass'# and #Q_'JOB OPPORTUNITY: Culling the Competition'#." },
			[10] = { str = "Accept #Q_'Curse These Fat Fingers'# #Q_'Fiery Menace!'# #Q_'Incendosaurs? Whateverosaur is More Like It'# near the tower." },

			[11] = { str = "Under the tent accept #Q_'What The Flux?'#." },
			[12] = { str = "Unlock the flight path by talking to the #N_Flight Master# and fly to #A_Ironforge#." },
			[13] = { str = "Stop at #C_[71,16]# and turn in #Q_'Suntara Stones'#(2) accept #Q_'Dwarven Justice'#.", x = 71, y = 16, zone = "Searing Gorge" },
			[14] = { str = "Fly to #A_Thelsamar# (#A_Loch Modan#)." },
			[15] = { str = "Run down to #C_[18,83]# and turn in #Q_'The Horn of the Beast'#. Accept #Q_'Proof of Deed'#.", x = 18, y = 83, zone = "Searing Gorge" },
			[16] = { str = "Use your #I_[Hearthstone]# to return to #A_Ironforge#." },
			[17] = { str = "Go to #C_[69,11]# turn in, #Q_'Proof of Deed'#, accept #Q_'At Last!'#", x = 69, y = 11, zone = "Searing Gorge" },
			[18] = { str = "Go to #C_[75,23]# and accept #Q_'A Little Slime Goes A Long Way'#.", x = 75, y = 23, zone = "Searing Gorge" },
			[19] = { str = "Fly to #A_Thelsamar# (#A_Loch Modan#) and run to #C_[18,83]#.", x = 18, y = 83, zone = "Searing Gorge" },
			[20] = { str = "Turn in #Q_'At Last!'# put the key on your keyring. Easy 12k xp." },

			[21] = { str = "Now the fun part, doing all these quests. Some of the mobs you have to kill are in a widespread area so I really won’t list coords on them." },
			[22] = { str = "From here south kill glassweb spiders for #Q_'Ledger From Tanaris'#." },
			[23] = { str = "Kill any golems you see over here for #Q_'The Flawless Flame'#." },
			[24] = { str = "Go around the southern part of the cauldron (the pit) and circle it back towards #A_Thorium Point# (#A_Searing Gorge#) and do the following:" },
			[25] = { str = "Kill heavy war golems and infernal elementals (not magma elementalsfor #Q_'The Flawless Flame'# and heavy war golems for #Q_'Curse These Fat Fingers'#." },
			[26] = { str = "Kill dark iron lookouts and steamsmiths (Only 6 of them, so come back if they don’t drop, they seem to drop outhouse key a lot) for #Q_'STOLEN: Smithing Tuyere'# and #Q_'Lookout's Spyglass'#." },
			[27] = { str = "Turn in #Q_'The Flawless Flame'#. Accept #Q_'Forging the Shaft'# when you’re near #A_Thorium Point# (#A_Searing Gorge#) again." },
			[28] = { str = "Kill greater lava spiders (not searing) for #Q_'Fiery Menace!'# Mostly SW." },
			[29] = { str = "If the Grimesilt Outhouse Key dropped, use it to start #Q_'The Key to Freedom'# and turn it in at #C_[65,62]#.", x = 65, y = 62, zone = "Searing Gorge" },
			[30] = { str = "Go to #C_[63,38]# and start going down into the cauldron.", x = 63, y = 38, zone = "Searing Gorge" },

			[31] = { str = "Kill dark iron taskmasters and slavers down here and inside the cave if you see them for #Q_'JOB OPPORTUNITY: Culling the Competition'# and #Q_'Forging the Shaft'#." },
			[32] = { str = "Enter the caves at #C_[41,54]#.", x = 41, y = 54, zone = "Searing Gorge" },
			[33] = { str = "Go in the cave to #C_[41,25]# and go through the jail bar door, turn in #Q_'Dwarven Justice'#. Accept #Q_'Release Them'#.", x = 41, y = 25, zone = "Searing Gorge" },
			[34] = { str = "Go up the ramp and stay right until #C_[40,35]# kill the boss here for #Q_'WANTED: Overseer Maltorius'# and grab #Q_'What The Flux?'# on the rail behind him. If you can’t find a group have the pet get the aggro and run the guys away from the plans. This is doable with 2 people but very tough. If you can’t get this quest done no big deal but I usually get it done.", x = 40, y = 35, zone = "Searing Gorge" },
			[35] = { str = "Keep following the path that goes outside and curves back in. You’ll come to a big dead end area with lots of Incendosaur for #Q_'Incendosaurs? Whateverosaur is More Like It'# Save the scales." },
			[36] = { str = "Grind until youre 36k from leveling." },
			[37] = { str = "Die on purpose so you end up back at #A_Thorium Point# (#A_Searing Gorge#) and turn in these quests." },
			[38] = { str = "Go next to the sign, turn in #Q_'WANTED: Overseer Maltorius'#." },
			[39] = { str = "To the right of the tower, turn in #Q_'Curse These Fat Fingers'# #Q_'Fiery Menace!'# and #Q_'Incendosaurs? Whateverosaur is More Like It'#." },
			[40] = { str = "Go to the tent behind here, turn in #Q_'STOLEN: Smithing Tuyere'# and #Q_'Lookout's Spyglass'# and #Q_'JOB OPPORTUNITY: Culling the Competition'#." },

			[41] = { str = "Go to the next tent, turn in #Q_'What The Flux?'#." },
			[42] = { str = "Once this is all handed in you can now do repeat quests for small xp and faction with #A_Thunder Bluff#. The reason I said to save the scales or sell em." },
			[43] = { str = "You should be a hair from 51 now, go south of base to #C_[38,38]#.", x = 38, y = 38, zone = "Searing Gorge" },
			[44] = { str = "Turn in #Q_'Forging the Shaft'#. Accept #Q_'The Flame’s Casing'#." },
			[45] = { str = "Now you shoud be between 1-2 bars into 51." },
			[46] = { str = "Run over to the path up the mountain at #C_[24,32]#.", x = 24, y = 32, zone = "Searing Gorge" },
			[47] = { str = "Kill the elites 1 by 1 on your way up to the lava pit at #C_[29,25]# and clear the mobs around it, the idolators don’t chain link and don’t attack when you touch the rock in the center. Jump on the rock either with mount or jump in lava then on it. Open it for #Q_'Release Them'#.", x = 29, y = 25, zone = "Searing Gorge" },
			[48] = { str = "While killing these guys around the pool you should have found a Symbol of Ragnaros for #Q_'The Flame’s Casing'# if not kill until you do." },
			[49] = { str = "Make sure you talk to the caged npc next to the lava, he gives you #Q_'Prayer to Elune'# Listen to his short story and turn it in for #Q_'Prayer to Elune'#(2) Now you have to kill these for the Rag dropping and the #Q_'Prayer to Elune'#(2), Easy drops." },
			[50] = { str = "Go down to #C_[38,38]# and turn in #Q_'The Flame’s Casing'#. Accept #Q_'The Torch of Retribution'# then turn it back in accept #Q_'The Torch of Retribution'#(2) then grab the torch in front of him and complete it and accept #Q_'Squire Maltrake'# and turn that back in and accept #Q_'Set Them Ablaze!'#", x = 38, y = 38, zone = "Searing Gorge" },

			[51] = { str = "Now equip the staff and run to each of the 4 towers at N:#C_[33,53]# W:#C_[36,60]# S:#C_[44,61]# E:#C_[50,54]# and either kill the guys downstairs, and run up and light the brazier." },
			[52] = { str = "When you get them all, look for a safe spot to jump down and go in the caves at #C_[41,54]# again and through the jail bar door at #C_[37,44]# and run to the room with the golem at #C_[41,25]# and touch the candle covered stone and turn in #Q_'Release Them'# SKIP the next part unless you wanna do it with a group, it’s very tough you have to fight that golem and a dwarf both elites.", x = 41, y = 54, zone = "Searing Gorge" },
			[53] = { str = "Die on purpose so you end up at #A_Thorium Point# (#A_Searing Gorge#) and go down to #C_[29,38]# and turn in #Q_'Set Them Ablaze!'# watch the cutscene as he turns into a dragon.", x = 29, y = 38, zone = "Searing Gorge" },
			[54] = { str = "Open the chest on the ground accept #Q_'Trinkets'#. then open it again and complete it. Open it and keep the Black Dragonflight Molt for later In the #A_Burning Steppes#." },
			[55] = { str = "Fly to the Blasted Lands and run up into the Swamp of Sorrows." },
			[56] = { str = "Go to #C_[81,91]# and look for a named murloc Jarquia for #Q_'Ledger From Tanaris'#.", x = 81, y = 91, zone = "Swamp of Sorrows" },
			[57] = { str = "Once you kill him use your #I_[Hearthstone]# to return to #A_Ironforge# then either get a portal to #A_Darnassus# or fly to #A_Menethil Harbor# (#A_The Wetlands#) then boat to #A_Auberdine# (#A_Darkshore#) and fly to #A_Darnassus#." },
			[58] = { str = "Go to the top of the temple of the moon at #C_[38,80]# and turn in #Q_'Prayer to Elune'#(2).", x = 38, y = 80, zone = "Darnassus" },
			[59] = { str = "Go to the next section up here and turn in #Q_'Rise of the Silithid'#. Accept #Q_'March of the Silithid'#." },
			[60] = { str = "Go to #C_[34,9]# at the top of the tower and turn in #Q_'Assisting Arch Druid Staghelm'#. Accept #Q_'Un’Goro Soil'#.", x = 34, y = 9, zone = "Darnassus" },

			[61] = { str = "Talk to the #N_Innkeeper# to make #A_Darnassus# your home at #C_[67,15]#.", x = 67, y = 15, zone = "Darnassus" },
			[62] = { str = "Now either fly to #A_Theramore# (#A_Dustwallow Marsh#) or boat to #A_Menethil Harbor# then to #A_Theramore# (#A_Dustwallow Marsh#)." },
			[63] = { str = "Get first aid up to 290 if you haven’t for heavy runecloth bandages." },
			[64] = { str = "Go over to #C_[54,55]# to the wrecked ship and grab the overdue package for #Q_'Ledger From Tanaris'#.", x = 54, y = 55, zone = "Duskwallow Marsh" },
			[65] = { str = "Go to the cave at #C_[31,66]# and touch the scroll in the back of the cave which turns in #Q_'Cortello's Riddle'#(2) accept #Q_'Cortello's Riddle'#(3).", x = 31, y = 66, zone = "Duskwallow Marsh" },
			[66] = { str = "Aggro all the raptors and die so you end up at #A_Theramore# (#A_Dustwallow Marsh#)." },
			[67] = { str = "Fly to #A_Gadgetzan# (#A_Tanaris#)." },
			[68] = { str = "Just as you enter #A_Gadgetzan# (#A_Tanaris#), turn in #Q_'Ledger From Tanaris'#." },
			[69] = { str = "Get your level 52 set gloves out of bank or mail for later." },
			[70] = { str = "Between the 2 North gates accept #Q_'Super Sticky'#." },

			[71] = { str = "Go to the hut just west and turn in #Q_'March of the Silithid'#. Accept #Q_'Bungle in the Jungle'#." },
			[72] = { str = "Run down into the #A_Un’Goro Crater# at #C_[26,52]#.", x = 26, y = 52, zone = "Tanaris" },
		}
	},

-----------51-52 Un’Goro Crater
	[5152] = {
		title = "51-52 Un’Goro Crater",
		items = {
			[1] = { str = "51-52 Un’Goro Crater" },
			[2] = { str = "Keep an eye out for the following items laying around the crafter: Power Crystals (need 7 of red,blue,green,yellow), Un’Goro Dirt Piles (get as many as you can), and Bloodpetal Sprouts: save in sets of 15." },
			[3] = { str = "I tell you to grind through the raptors here only for 1 reason. To find the item A Mangled Journal. Once you have it you don’t have to grind through the raptors." },
			[4] = { str = "Follow the path down to the waterfall at #C_[71,75]# and accept #Q_'The Apes of Un’Goro'# and #Q_'The Fare of Lar'Korwi'#.", x = 71, y = 75, zone = "Un’Goro Crater" },
			[5] = { str = "Grind to #C_[62,68]# and click on the wrecked raft, accept #Q_'It's A Secret to Everybody'# then take 2 steps under the water, turn it in and accept #Q_'It's A Secret to Everybody'#(2).", x = 62, y = 68, zone = "Un’Goro Crater" },
			[6] = { str = "Grind to #C_[68,56]# and get the meat off the big dinosaur for #Q_'The Fare of Lar'Korwi'#.", x = 68, y = 56, zone = "Un’Goro Crater" },
			[7] = { str = "Go back to #C_[71,75]# and turn in #Q_'The Fare of Lar'Korwi'#. Accept #Q_'The Scent of Lar'Korwi'#.", x = 71, y = 75, zone = "Un’Goro Crater" },
			[8] = { str = "Grind west to about #C_[67,63]# and there will be 2 spots with a bunch of eggs. Clear the raptors around it and stand on the eggs to get the mates to spawn for #Q_'The Scent of Lar'Korwi'#.", x = 67, y = 63, zone = "Un’Goro Crater" },
			[9] = { str = "Once you have this done, go back to #C_[71,75]# and turn in #Q_'The Scent of Lar'Korwi'#. Accept #Q_'The Bait For Lar'Korwi'# you should have found the jornal by now as well, if not keep grinding raptors while you’re questing in this spot. If you have start the quest #Q_'Williden’s Journal'#.", x = 71, y = 75, zone = "Un’Goro Crater" },
			[10] = { str = "Go up to #C_[79,49]# it’s off to the east side in the mountains, behind the east pylon. You’ll see a big skeleton which is also the #A_Graveyard#. There is a fairly big flat stone, stand on it and use the meat and phermone. This will summon Lar’Korwi for #Q_'The Bait For Lar'Korwi'# super easy kill even at 51 and he’s 56, he has 3600 hp.", x = 79, y = 49, zone = "Un’Goro Crater" },

			[11] = { str = "Go to #C_[46,13]# and accept #Q_'Chasing A-Me 01'# and run up the path behind her into Marshal’s Refuge.", x = 46, y = 13, zone = "Un’Goro Crater" },
			[12] = { str = "Just to the left accept #Q_'Shizzle's Flyer'#." },
			[13] = { str = "Next to the left, a little bit back, accept #Q_'Muigin and Larion'#." },
			[14] = { str = "Next, near the sign accept #Q_'Lost!'# and on the sign accept #Q_'Beware of Pterrordax'#." },
			[15] = { str = "In front of the cave, accept #Q_'Roll the Bones'#." },
			[16] = { str = "If you have at least 15 bloodpetal sprouts, turn them in at the kodo for the repeatable quest #Q_'Dadanga is Hungry!'# you can get a very expensive recipe but it’s rare but worth trying." },
			[17] = { str = "To the right of the cave, accept #Q_'Alien Ecology'#." },
			[18] = { str = "To the right again, turn in #Q_'Williden’s Journal'#. Accept #Q_'Expedition Salvation'#." },
			[19] = { str = "Run just south a bit and you’ll find Linken just outside of a half hollow tree. Turn in #Q_'It's A Secret to Everybody'#(2) accept #Q_'It's A Secret to Everybody'#(3)." },
			[20] = { str = "Unlock the flight path by talking to the #N_Flight Master# to the right of the cave (added in 1.11) then go in the cave all the way to the end and accept #Q_'Crystals of Power'# and then turn it in Accept the 3 pylon quests #Q_'The Northern Pylon'# #Q_'The Eastern Pylon'# #Q_'The Western Pylon'#." },

			[21] = { str = "When ever you see the following mobs kill them on site until the quest associate with them is finished." },
			[22] = { str = "Kill bloodpetals for #Q_'Muigin and Larion'#." },
			[23] = { str = "Kill diametradons and pterrodaxes for #Q_'Shizzle's Flyer'# and #Q_'Roll the Bones'#." },
			[24] = { str = "Kill pterrodaxes and frenzied pterrodaxes for #Q_'Beware of Pterrordax'#." },
			[25] = { str = "Go east along the mountains to #C_[54,13]# and take that path up examine the pylon for #Q_'The Northern Pylon'# and go farther north, there should be sone pterrodaxes.", x = 54, y = 13, zone = "Un’Goro Crater" },
			[26] = { str = "Go east to #C_[63,16]# and kill gorillas for #Q_'The Apes of Un’Goro'# then go in the cave and stay right to turn in #Q_'Chasing A-Me 01'#. SKIP the next part for now.", x = 63, y = 16, zone = "Un’Goro Crater" },
			[27] = { str = "You’re gonna see a bunch of black lakes near the north of the map around #C_[52,23]# Kill the tar monsters around the lakes for #Q_'Super Sticky'# The 3 lakes south of the gorillas around #C_[61,27]# have lower level tar monsters and there is also bloodpetals around here for #Q_'Muigin and Larion'# there is also diametradons and pterrodaxes for #Q_'Shizzle's Flyer'# running around.", x = 61, y = 27, zone = "Un’Goro Crater" },
			[28] = { str = "Stop at #C_[68,36]# and grab the crate of foodstuff for #Q_'Expedition Salvation'#.", x = 68, y = 36, zone = "Un’Goro Crater" },
			[29] = { str = "Try to collect all the stuff for #Q_'Shizzle's Flyer'# out here if you aren’t just going around looking for them alone. I always seem to find them all before I finish the bloodpetals." },
			[30] = { str = "Go to #C_[76,51]# and follow the path up to the pylon for #Q_'The Eastern Pylon'# and there should be pterrodaxes further south at #C_[75,61]#.", x = 76, y = 51, zone = "Un’Goro Crater" },

			[31] = { str = "Go to #C_[71,75]# and turn in' #Q_'The Bait For Lar'Korwi'# and #Q_'The Apes of Un’Goro'# SKIP the next part for now.", x = 71, y = 75, zone = "Un’Goro Crater" },
			[32] = { str = "Now if you grinded a whole lot like I did on those damn flowers and dinosaurs, you should be really close to 52 if not 52 now." },
			[33] = { str = "Just east of the volcano (center of map) there are pterrodaxes all for #Q_'Beware of Pterrordax'# if you’re having trouble finding them there is a spawn of 6 of them all the way south at #C_[55,90]#.", x = 55, y = 90, zone = "Un’Goro Crater" },
			[34] = { str = "Run west to #C_[55,73]# there is a diametradon camp of about 5-6 Kill these if you need scales still for #Q_'Shizzle's Flyer'# or #Q_'Roll the Bones'#.", x = 55, y = 73, zone = "Un’Goro Crater" },
			[35] = { str = "Go west to #C_[50,77]# into the slithering scar, at the fork go left into the big room, stand in the middle, use the scraping vial for #Q_'Alien Ecology'#.", x = 50, y = 77, zone = "Un’Goro Crater" },
			[36] = { str = "There is another diametradon camp if you still need scales or bones for #Q_'Shizzle's Flyer'# or #Q_'Roll the Bones'# and they’re also all over now." },
			[37] = { str = "Run to the camp at #C_[38,65]# and grab the research equipment for #Q_'Expedition Salvation'#.", x = 38, y = 65, zone = "Un’Goro Crater" },
			[38] = { str = "Kill frenzied pterrodaxes all around #C_[35,38]# (they’re everywhere) in the NW part of the map for #Q_'Beware of Pterrordax'# plus there are many diametradons here for #Q_'Shizzle's Flyer'# or #Q_'Roll the Bones'#.", x = 35, y = 38, zone = "Un’Goro Crater" },
			[39] = { str = "Go down to #C_[23,59]# and touch the western pylon for #Q_'The Western Pylon'# and kill any pterrodaxes you need behind it.", x = 23, y = 59, zone = "Un’Goro Crater" },
			[40] = { str = "Go to #C_[30,50]# and accept #Q_'Finding the Source'#.", x = 30, y = 50, zone = "Un’Goro Crater" },

			[41] = { str = "Go over to Fire Plume Ridge at #C_[51,48]# and kill elementals, try to avoid anything 56+ for #Q_'Volcanic Activity'# Leave blazing invaders alone.", x = 51, y = 48, zone = "Un’Goro Crater" },
			[42] = { str = "Once you have that done, dismiss pet and mount up towards the top of the volcano and look for what looks like the instance waiting stones but with fire cracks in it. Dismount and FD then if there is a mob near it put pet on it and use the thermometer for #Q_'Finding the Source'# there is more than one and it doesn’t have to be at the top I see them at the bottom too." },
			[43] = { str = "Unless you need ashes still run to #C_[30,50]# and turn in #Q_'Finding the Source'#. Accept #Q_'The New Springs'#.", x = 30, y = 50, zone = "Un’Goro Crater" },
			[44] = { str = "Go up into the cave (in the same manner) at #C_[51,49]# and turn in #Q_'Lost!'# then accept #Q_'A Little Help From My Friends'# he follows you, once you get him out of the ridge it’s easy. Just go straight out the cave over the edge, if done right you only have to fight 1 element. Once you go north past the river it’s pie.", x = 51, y = 49, zone = "Un’Goro Crater" },
			[45] = { str = "When you get him back to town run up near the sign by the cave and it will complete then turn it in and #Q_'Beware of Pterrordax'#." },
			[46] = { str = "Turn in #Q_'Shizzle's Flyer'#." },
			[47] = { str = "Turn in #Q_'Muigin and Larion'# SKIP the rest." },
			[48] = { str = "Turn in #Q_'Roll the Bones'#." },
			[49] = { str = "Turn in #Q_'Alien Ecology'#." },
			[50] = { str = "Turn in #Q_'Expedition Salvation'#." },

			[51] = { str = "In the back of the cave turn in #Q_'The Northern Pylon'# #Q_'The Eastern Pylon'# #Q_'The Western Pylon'#. Accept #Q_'Making Sense of It'# then turn it back in. She then tells you about buffs from the pylons." },
			[52] = { str = "Run to #C_[29,22]# and follow the path into #A_Silithus# to #C_[50,34]#. Unlock the flight path by talking to the #N_Flight Master# and fly to #A_Gadgetzan# (#A_Tanaris#).", x = 29, y = 22, zone = "Un’Goro Crater" },
			[53] = { str = "Go to the NW part of town, turn in #Q_'Bungle in the Jungle'# SKIP the next part then hand in #Q_'Super Sticky'# between the 2 entrances." },
			[54] = { str = "Use your #I_[Hearthstone]# to return to #A_Darnassus# and talk to your #N_Class Trainer# to learn new spells and abilities." },
			[55] = { str = "You should have at least 50+ soils easy now." },
			[56] = { str = "Go to #C_[31,8]# and turn in #Q_'Un’Goro Soil'#.", x = 31, y = 8, zone = "Darnassus" },
			[57] = { str = "Now go up to the top of the tower and accept #Q_'Morrowgrain Research'# then go downstairs and turn it in, accept #Q_'Morrowgrain Research'#(2)." },
