			[4] = { str = "In front of the broken house, accept #Q_'Dragonkin Menace'# This is the start of the ony key chain." },
			[5] = { str = "Start killing ogres all over the mountain at #C_[81,42]# for #Q_'Extinguish the Firegut'# then run back to #C_[84,68]# and turn it in, accept #Q_'Gor'tesh the Brute Lord'#.", x = 81, y = 42, zone = "Burning Steps" },
			[6] = { str = "Go NW to around #C_[66,40]# and collect the ruins that look like tombstones for #Q_'The Smoldering Ruins of Thaurissan'#(2).", x = 66, y = 40, zone = "Burning Steps" },
			[7] = { str = "Run up to #C_[65,24]# and go in the cave, turn in #Q_'Yuka Screwspigot'# SKIP the rest.", x = 65, y = 24, zone = "Burning Steps" },
			[8] = { str = "Outside accept #Q_'Tablet of the Seven'# and #Q_'Broodling Essence'#." },
			[9] = { str = "Go to #C_[54,40]# and touch the dwarf statue for #Q_'Tablet of the Seven'#.", x = 54, y = 40, zone = "Burning Steps" },
			[10] = { str = "Go west to #C_[43,46]# to blackrock stronghold or south to #C_[49,55]# the pillar of ash and kill the orcs for #Q_'FIFTY! YEP!'# they drop in large amounts.", x = 43, y = 46, zone = "Burning Steps" },

			[11] = { str = "While in the pillar of ash at #C_[40,56]# you should kill Gor’tesh for #Q_'Gor'tesh the Brute Lord'#.", x = 40, y = 56, zone = "Burning Steps" },
			[12] = { str = "Get a group if you can for #Q_'Dragonkin Menace'# and then kill the mobs for it just ne of the camp along the east wall. It’s soloable but cutting it Also get 8 of the broodlings for #Q_'Broodling Essence'# by using the device on them before you attack them." },
			[13] = { str = "Go to the cave at #C_[94,31]# and accept #Q_'A Taste of Flame'# then give him the molt and hand the quest back in. SKIP the next part.", x = 94, y = 31, zone = "Burning Steps" },
			[14] = { str = "Run back up to #C_[65,23]# and turn in #Q_'Tablet of the Seven'# and #Q_'Broodling Essence'# accept #Q_'Felnok Steelspring'#.", x = 65, y = 23, zone = "Burning Steps" },
			[15] = { str = "Ride back to #C_[84,68]# and next to the griffin turn in #Q_'FIFTY! YEP!'# and #Q_'Gor'tesh the Brute Lord'# accept #Q_'Ogre Head On A Stick = Party'#.", x = 84, y = 68, zone = "Burning Steps" },
			[16] = { str = "In front of the broken house, turn in #Q_'Dragonkin Menace'# accept #Q_'The True Masters'#." },
			[17] = { str = "Run up to the top of the mountin at #C_[80,45]# and click the soft dirt pile for #Q_'Ogre Head On A Stick = Party'# then run back to #C_[84,68]# and turn it in.", x = 80, y = 45, zone = "Burning Steps" },
			[18] = { str = "Fly to #A_Lakeshire# (#A_Redridge Mountains#) and run to the town hall at #C_[29,44]# and turn in #Q_'The True Masters'# accept #Q_'The True Masters'#(2).", x = 29, y = 44, zone = "Redridge Mountains" },
			[19] = { str = "Fly to #A_Stormwind#." },
			[20] = { str = "Go to #C_[78,18]# into the #A_Stormwind# keep and turn in #Q_'The True Masters'#(2) accept #Q_'The True Masters'#(3) then talk to the lady and then turn it back in and accept #Q_'The True Masters'#(4).", x = 78, y = 18, zone = "Stormwind City" },

			[21] = { str = "Fly back to #A_Lakeshire# (#A_Redridge Mountains#) and go to the townhall at #C_[29,44]# and turn in #Q_'The True Masters'#(4) accept #Q_'The True Masters'#(5).", x = 29, y = 44, zone = "Redridge Mountains" },
			[22] = { str = "Fly to the #A_Morgan's Vigil# (#A_Burning Steppes#) and turn in #Q_'The True Masters'#(5) when you land and accept #Q_'The True Masters'#(6)." },
			[23] = { str = "Run up to #C_[64,23]# and listen to ragged john then run back to #C_[84,68]# and turn in #Q_'The True Masters'#(6) I SKIP the next part, do it later if you’re worried about speed leveling. That was an easy 30k xp for doing nothing and your ony key chain is started.", x = 64, y = 23, zone = "Burning Steps" },
			[24] = { str = "You should be about 10k from 56, go grind until 5k on the orcs at #C_[49,55]#.", x = 49, y = 55, zone = "Burning Steps" },
			[25] = { str = "Use your #I_[Hearthstone]# to return to #A_Ironforge#." },
			[26] = { str = "Go to the throne room at #C_[38,55]# and turn in #Q_'The Smoldering Ruins of Thaurissan'#(2).", x = 38, y = 55, zone = "Ironforge" },
			[27] = { str = "You should now be 56, talk to your #N_Class Trainer# to learn new spells and abilities." },
			[28] = { str = "Fly to #A_Menethil Harbor# (#A_The Wetlands#) and boat to #A_Theramore# (#A_Dustwallow Marsh#)." },
			[29] = { str = "Fly to #A_Gadgetzan# (#A_Tanaris#)." },
		}
	},

