-----------56-56 Silithus
	[5656] = {
		title = "56-56 Silithus",
		items = {
			[1] = { str = "56-56 Silithus" },
			[2] = { str = "Near the moonwell accept #Q_'The Twilight Mystery'#." },
			[3] = { str = "In front of the Inn, turn in #Q_'Taking Back Silithus'# accept #Q_'Securing the Supply Lines'#." },
			[4] = { str = "SKIP the quest on the sign unless you get a group." },
			[5] = { str = "Talk to the #N_Innkeeper# to make #A_Cenarion Hold# (#A_Silithus#) your home." },
			[6] = { str = "Upstairs accept #Q_'Deadly Desert Venom'#." },
			[7] = { str = "There seems to be a lot of white ! because that’s a lot of AQ stuff you don’t have to worry about yet." },
			[8] = { str = "Go do #Q_'Securing the Supply Lines'# and #Q_'Deadly Desert Venom'# North and NE of town all around #C_[55,24]# the dredges, scorpids, and skitterers for these 2 quests are all over this area.", x = 55, y = 24, zone = "Silithus" },
			[9] = { str = "Run up to #C_[81,18]# and turn in #Q_'Wasteland'# accept #Q_'The Spirits of Southwind'#.", x = 81, y = 18, zone = "Silithus" },
			[10] = { str = "Run up NW to around 22.9 and pick up the twilight tablet fragments for #Q_'The Twilight Mystery'# They litter this spot.", x = 22, y = 9, zone = "Silithus" },

			[11] = { str = "Use your #I_[Hearthstone]# to return to Cenarion Hold." },
			[12] = { str = "Up stairs turn in #Q_'Deadly Desert Venom'# accept #Q_'Noggle's Last Hope'#." },
			[13] = { str = "In front of the Inn, turn in #Q_'Securing the Supply Lines'# accept #Q_'Stepping Up Security'#." },
			[14] = { str = "Go next to the moonwell and turn in #Q_'The Twilight Mystery'# accept #Q_'The Deserter'#." },
			[15] = { str = "You should be about 30-35% into 56." },
			[16] = { str = "Go SE to #C_[62,53]# and kill the undead guys here for #Q_'The Spirits of Southwind'# becareful when they die they spawn 1-2 bugs.", x = 62, y = 53, zone = "Silithus" },
			[17] = { str = "Kill the pincers for #Q_'Noggle's Last Hope'# and drudges for #Q_'The Deserter'# south of here around #C_[56,58]#.", x = 56, y = 58, zone = "Silithus" },
			[18] = { str = "Go to the cave at #C_[67,69]# and turn in #Q_'The Deserter'# accept #Q_'The Twilight Lexicon'#.", x = 67, y = 69, zone = "Silithus" },
			[19] = { str = "You can find flayers and stalkers outside of the cave for #Q_'Noggle's Last Hope'# they are also both around #C_[43,72]# on the other side of the hive.", x = 43, y = 72, zone = "Silithus" },
			[20] = { str = "Use your #I_[Hearthstone]# to return to back to Cenarion Hold or die so you end up there." },

			[21] = { str = "Go upstairs and turn in for #Q_'Noggle's Last Hope'# accept #Q_'Noggle's Lost Satchel'#." },
			[22] = { str = "Outside the Inn, turn in #Q_'Stepping Up Security'#." },
			[23] = { str = "Go to #C_[29,35]# and kill Twilight Keeper Manya for #Q_'The Twilight Lexicon'# she fears a bit. She’s at the SE corner of came.", x = 29, y = 35, zone = "Silithus" },
			[24] = { str = "Go to #C_[41,41]# and kill Twilight Keeper Havanuth for #Q_'The Twilight Lexicon'# he’s on the east side of camp, he has a nasty aoe.", x = 41, y = 41, zone = "Silithus" },
			[25] = { str = "Go all the way down south to the little camp at #C_[42,90]# and go up the hill behind it, you’ll find Deathclasp, in the middle of the path on the wall is a pouch, grab it when deathclasp is in the back for #Q_'Noggle's Lost Satchel'#.", x = 42, y = 90, zone = "Silithus" },
			[26] = { str = "Head to the SW to #C_[16,87]# and kill Twilight Keeper Exeter for #Q_'The Twilight Lexicon'# he’s at the SW corner of camp.", x = 16, y = 87, zone = "Silithus" },
			[27] = { str = "Go back to the cave at #C_[67,69]# and turn in #Q_'The Twilight Lexicon'# accept #Q_'A Terrible Purpose'# He also offers another quest for 10 twilight texts, this is more for cen circle rep later on.", x = 67, y = 69, zone = "Silithus" },
			[28] = { str = "Run up to #C_[81,18]# and turn in #Q_'The Spirits of Southwind'# accept #Q_'Hive in the Tower'#.", x = 81, y = 18, zone = "Silithus" },
			[29] = { str = "Go to the top of the tower at #C_[61,52]# Be careful 3 bugs spawn when you enter. Shift right click the pod because as soon as you open it some amushers spawn. Kill at least 1 and loot it for #Q_'Hive in the Tower'#.", x = 61, y = 52, zone = "Silithus" },
			[30] = { str = "Go up to #C_[81,18]# and turn in #Q_'Hive in the Tower'# accept #Q_'Umber, Archivist'#.", x = 81, y = 18, zone = "Silithus" },

			[31] = { str = "Use your #I_[Hearthstone]# to return to Cenarion Hold." },
			[32] = { str = "Ignore." },
			[33] = { str = "Go upstairs and turn in #Q_'Noggle's Lost Satchel'#." },
			[34] = { str = "Go into the long building and turn in #Q_'A Terrible Purpose'#." },
			[35] = { str = "Fly to Moonglade." },
			[36] = { str = "Go to #C_[44,35]# and turn in #Q_'Umber, Archivist'# accept #Q_'Uncovering Past Secrets'#.", x = 44, y = 35, zone = "Moonglade" },
			[37] = { str = "Run over to #C_[51,44]# and turn in #Q_'Uncovering Past Secrets'#.", x = 44, y = 35, zone = "Moonglade" },
			[38] = { str = "You should now be 80-85% into leveling." },
			[39] = { str = "Fly to #A_Auberdine# (#A_Darkshore#) and boat to #A_Menethil Harbor# (#A_The Wetlands#) then fly to #A_Ironforge#." },
			[40] = { str = "Talk to the #N_Innkeeper# to make #A_Ironforge# your home. Find the wandering commander dwarf and accept #Q_'A Call To Arms: The Plaguelands!'#" },

			[41] = { str = "Fly to #A_Southshore# (#A_Hillsbrad Foothills#)." },
			[42] = { str = "Run north into Alterac Mountains and over NE to #C_[80,34]# and run into Western Plaguelands (WPL).", x = 80, y = 34, zone = "Alterac Mountains" },
		}
	},

------------56-57 Western Plaguelands
	[5657] = {
		title = "56-57 Western Plaguelands",
		items = {
			[1] = { str = "56-57 Western Plaguelands" },
			[2] = { str = "Next to the shack accept #Q_'A Plague Upon Thee'#." },
			[3] = { str = "Unlock the flight path by talking to the #N_Flight Master#." },
			[3] = { str = "Whenever you come back to camp here always check the vendor for the stormshroud pants recipe. It’s like 1.3g and you can easily sell it for 10 or 20g+ It’s a limited recipe that restocks 1 every hour." },
			[4] = { str = "Go to the tent and turn in #Q_'A Call To Arms: The Plaguelands!'#, accept #Q_'Clear The Way'#." },
			[5] = { str = "Go to the 3 Argent Dawn people near the other tent and turn in #Q_'The Everlook Report'# and also complete #Q_'Argent Dawn Commission'# it’s not a quest you had she just uses it to give you the trinket which you should always wear when in WPL and EPL because it gives you AD rep and allows the scourge tokens to drop, which she also has 3 new quests on those for turn ins. You most likely won’t get anything past the first quest as the rest are mainly in the instances." },
			[6] = { str = "Save the tokens until I say to hand em in." },
			[7] = { str = "Go NE to around #C_[51,78]# and grind the ghouls here until you got enough for #Q_'Clear The Way'#.", x = 51, y = 78, zone = "Western Plaguelands" },
			[8] = { str = "Go back to Chillwind Camp at #C_[42,84]# and turn in #Q_'Clear The Way'# accept #Q_'All Along the Watchtowers'# and #Q_'The Scourge Cauldrons'#.", x = 42, y = 84, zone = "Western Plaguelands" },
			[9] = { str = "Turn around and turn in #Q_'The Scourge Cauldrons'# accept #Q_'Target: Felstone Field'#." },
			[10] = { str = "You should now be about 5-10k from 57." },

			[11] = { str = "Go back NE to #C_[48,72]# and go over the bridge.", x = 48, y = 72, zone = "Western Plaguelands" },
			[12] = { str = "Just in the town on the left at #C_[47,71]# is the 4th tower for #Q_'All Along the Watchtowers'#, there are elites inside but you can stand out of LOS on the edge and mark the doors easily. 1st tower West of here at #C_[40,71]#. 2nd tower NE of here at #C_[42,66]#. 3rd tower NE of here at #C_[44,63]#. Just behind this tower.", x = 47, y = 71, zone = "Western Plaguelands" },
			[13] = { str = "Go NW to #C_[37,56]# to Felstone field, kill Cauldron Lord Bilemaw for his cauldron key. Then go right click the cauldron and turn in #Q_'Target: Felstone Field'# accept #Q_'Return to Chillwind Camp'#.", x = 37, y = 56, zone = "Western Plaguelands" },
			[14] = { str = "Theres 2 houses on the NE side of the field at #C_[38,54]#, enter the northern house and go upstairs. Accept #Q_'Better Late Than Never'#.", x = 38, y = 54, zone = "Western Plaguelands" },
			[15] = { str = "Go in the barn next door and grab the box on the floor and turn in #Q_'Better Late Than Never'# accept #Q_'Better Late Than Never'#(2)." },
			[16] = { str = "Run back to Chillwind Camp at #C_[42,84]# and turn in #Q_'All Along the Watchtowers'#, accept #Q_'Scholomance'# SKIP the other unless you get about 10-20 people.", x = 42, y = 84, zone = "Western Plaguelands" },
			[17] = { str = "Turn in #Q_'Scholomance'# just behind him, accept #Q_'Skeletal Fragments'# Bones for this will take a while and drop off any skeletons." },
			[18] = { str = "Near the flight path turn in #Q_'Return to Chillwind Camp'# accept #Q_'Target: Dalson's Tears'#." },
			[19] = { str = "Go up to #C_[46,52]# to Dalson’Tears, kill Cauldron Lord Malvinious for his cauldron key. Then go right click the cauldron and turn in #Q_'Target: Dalson's Tears'# accept #Q_'Return to Chillwind Camp'#.", x = 46, y = 52, zone = "Western Plaguelands" },
			[20] = { str = "Enter the barn here at #C_[47,50]# and Touch the diary on the floor and complete it, another quest that isn’t a quest.", x = 47, y = 50, zone = "Western Plaguelands" },

			[21] = { str = "Go behind the barn and look for a Wandering Skeleton and get Dalson Outhouse Key off of him. Use this to open the outhouse back here and kill Farmer Dalson and get his Dalson Cabinet Key. Use the key on the locked cabinet upsairs in the house now at #C_[47,49]# next to the barn and complete the quest #Q_'Locked Away'#.", x = 47, y = 49, zone = "Western Plaguelands" },
			[22] = { str = "Go back to Chillwind Camp at #C_[42,84]# and turn in #Q_'Return to Chillwind Camp'# accept #Q_'Target: Writhing Haunt'#.", x = 42, y = 84, zone = "Western Plaguelands" },
			[23] = { str = "Use your #I_[Hearthstone]# to return to #A_Ironforge# then fly to #A_Stormwind#." },
			[24] = { str = "Go to the building at #C_[48,30]# on the 2nd floor of the cathedral area and turn in #Q_'Better Late Than Never'#(2) accept #Q_'Good Natured Emma'#.", x = 48, y = 30, zone = "Stormwind" },
			[25] = { str = "Emma travels between the cathedral and the trade district, find her and turn in #Q_'Good Natured Emma'# accept #Q_'Good Luck Charm'#." },
			[26] = { str = "Go into the keep at #C_[78,17]# and accept #Q_'The First and the Last'#.", x = 78, y = 17, zone = "Stormwind" },
			[27] = { str = "Go to #C_[75,79]# and turn in #Q_'The First and the Last'# accept #Q_'Honor the Dead'# then turn it right back in, accept #Q_'Flint Shadowmore'#.", x = 75, y = 79, zone = "Stormwind" },
			[28] = { str = "Fly back to WPL." },
			[29] = { str = "In front of the house turn in #Q_'Flint Shadowmore'# accept #Q_'The Eastern Plagues'#." },
			[30] = { str = "Go back to Felstone field to the house upstairs at #C_[38,54]# and turn in #Q_'Good Luck Charm'# accept #Q_'Two Halves Become One'# Go outside and look for a jabbering ghoul. You can’t miss him he carry’s a pitchfork. Keep clearing in front of the house until you find him. This will give you a good time to get those #Q_'Skeletal Fragments'#.", x = 38, y = 54, zone = "Western Plaguelands" },

			[31] = { str = "Combine the halves and go back upstairs and turn in #Q_'Two Halves Become One'#." },
			[32] = { str = "Go SE to #C_[53,65]# to Writhing Haunt, kill Cauldron Lord Razarch for his cauldron key. Then go right click the cauldron and turn in #Q_'Target: Writhing Haunt'# accept #Q_'Return to Chillwind Camp'#.", x = 53, y = 65, zone = "Western Plaguelands" },
			[33] = { str = "In the house at #C_[54,65]# accept #Q_'The Wildlife Suffers Too'# now go north of the haunt and kill the diseased wolves. Don’t go any farther east to find these just go North, maybe NW some.", x = 54, y = 65, zone = "Western Plaguelands" },
			[34] = { str = "Go back to #C_[54,65]# and turn in #Q_'The Wildlife Suffers Too'# accept #Q_'The Wildlife Suffers Too'#(2).", x = 54, y = 65, zone = "Western Plaguelands" },
			[35] = { str = "Go back to Chillwind Camp at #C_[42,84]# anc turn in #Q_'Return to Chillwind Camp'# accept #Q_'Target: Gahrron's Withering'#.", x = 42, y = 84, zone = "Western Plaguelands" },
			[36] = { str = "Go just north of the Writhing Haunt between the mountains on the map at, #C_[52,56]# and kill diseased grizzleys on your way down to #C_[62,58]# for #Q_'The Wildlife Suffers Too'#(2).", x = 52, y = 56, zone = "Western Plaguelands" },
			[37] = { str = "Go NE to #C_[62,58]# to Gahrron’s Withering, kill Cauldron Lord Soulwrath for his cauldron key. Then go right click the cauldron and turn in #Q_'Target: Gahrron's Withering'# accept #Q_'Return to Chillwind Camp'#.", x = 62, y = 58, zone = "Western Plaguelands" },
			[38] = { str = "Go back to #C_[54,65]# and turn in #Q_'The Wildlife Suffers Too'#(2) accept #Q_'Glyphed Oaken Branch'#.", x = 54, y = 65, zone = "Western Plaguelands" },
			[39] = { str = "If you haven’t finished #Q_'Skeletal Fragments'# yet go to the crypt at #C_[54,79]# and kill the skeletons until you have 15 fragments.", x = 54, y = 79, zone = "Western Plaguelands" },
			[40] = { str = "Go back to Chillwind Camp at #C_[42,84]# and turn in #Q_'Return to Chillwind Camp'#.", x = 42, y = 84, zone = "Western Plaguelands" },

			[41] = { str = "Turn around to the guy in front of the tent, and complete the quest #Q_'Mission Accomplished!'# no link for it cuz it’s not a quest again lol. Now see that running back and forth seemed out of the way but there was a huge xp boost at the end, and nice xp throughout." },
			[42] = { str = "To your right turn in #Q_'Skeletal Fragments'# SKIP the next part unless you want to start on your scholo key." },
			[43] = { str = "Run east to #C_[70,50]# and enter #A_Eastern Plaguelands#.", x = 70, y = 50, zone = "Western Plaguelands" },
		}
	},

-----------57-58 Eastern Plaguelands
	[5758] = {
		title = "57-58 Eastern Plaguelands",
		items = {
			[1] = { str = "57-58 Eastern Plaguelands" },
			[2] = { str = "Run up the west coast to #C_[67,4]# and accept the 3 quests #Q_'Demon Dogs'# #Q_'Blood Tinged Skies'# and #Q_'Carrion Grubbage'#.", x = 67, y = 4, zone = "Eastern Plaguelands" },
			[3] = { str = "These are annoying quests that can take a while. Anytime from now until these are done, kill any plaguehound runts, carrion worms, or plaguebats." },
			[4] = { str = "From here on down to the bigger area, kill any dogs, worms, and bats. Focus on them while you head to the next step." },
			[5] = { str = "Keep killing the dogs and bats and head over to the skeleton at #C_[28,74]# open it and grab the insignia for #Q_'The Eastern Plagues'# then run up a bit further to the next skeleton at #C_[27,74]# and grab the insignia, it’s practically right in front of him and you will also get the blightcaller found complete message. Now go down near the road to #C_[28,79]# and loot the last insignia off the skeleton.", x = 28, y = 74, zone = "Eastern Plaguelands" },
			[6] = { str = "Focus on the dogs and bats while you head to Darrowshire at #C_[36,90]# in the broken down house. Turn in #Q_'Sister Pamela'# accept #Q_'Pamela's Doll'# then head towards the houses here, in any of the houses can be any of the 3 doll parts. They are random. Ghosts spawn when you get near the parts so be aware. Once you have all 3 parts combine them and go back to #C_[36,90]# and turn in #Q_'Pamela's Doll'# accept #Q_'Auntie Marlene'# and #Q_'Uncle Carlin'#.", x = 36, y = 90, zone = "Eastern Plaguelands" },
			[7] = { str = "Look on your map at #C_[48,76]# This lines up with that middle ridge mountain. Don’t go past this spot east until you have your plaguehound runt kills for #Q_'Demon Dogs'# and plaguebats for #Q_'Blood Tinged Skies'# because there is none past that point.", x = 48, y = 76, zone = "Eastern Plaguelands" },
			[8] = { str = "Don’t forget to kill the grubs along the way too for #Q_'Carrion Grubbage'#, they’re not as important because they’re all over." },
			[9] = { str = "Kill Plaguehounds for #Q_'Demon Dogs'# all around #C_[56,57]# this is north of the scar and south of blackwood lake. Don’t forget the grubs.", x = 56, y = 57, zone = "Eastern Plaguelands" },
			[10] = { str = "Kill Frenzied Plaguehounds for #Q_'Demon Dogs'# all around #C_[61,41]# this is north of blackwood lake but now west of it.", x = 61, y = 41, zone = "Eastern Plaguelands" },

			[11] = { str = "Head to #A_Lights Hope Chapel# (#A_Eastern Plaguelands#) at #C_[81,58]# if you still need grub meat for #Q_'Carrion Grubbage'#, then kill them as you go.", x = 81, y = 58, zone = "Eastern Plaguelands" },
			[12] = { str = "Unlock the flight path by talking to the #N_Flight Master#." },
			[13] = { str = "Turn in #Q_'Uncle Carlin'# accept #Q_'Defenders of Darrowshire'#." },
			[14] = { str = "Turn around and turn in #Q_'Duke Nicholas Zverenhoff'#." },
			[15] = { str = "Run SW to #C_[79,63]# and accept #Q_'Zaeldarr the Outcast'#.", x = 79, y = 63, zone = "Eastern Plaguelands" },
			[16] = { str = "Fly back to EPL." },
			[17] = { str = "In front of the house turn in #Q_'The Eastern Plagues'# accept #Q_'The Blightcaller Cometh'#." },
			[18] = { str = "If you’re within 6600 XP to level then Use your #I_[Hearthstone]# to return to #A_Ironforge# and fly to #A_Stormwind#, if not then grind until you’re within range." },
			[19] = { str = "Go to #C_[78,18]# in the keep and turn in #Q_'The Blightcaller Cometh'# now I SKIP the next part but it’s a good quest to do at 60.", x = 78, y = 18, zone = "Eastern Plaguelands" },
			[18] = { str = "Talk to your #N_Class Trainer# to learn new spells and abilities." },
			[19] = { str = "Fly back to #A_Chillwind Point# (#A_Western Plaguelands#)." },
		}
	},

-----------58-58 Western Plaguelands
	[5857] = {
		title = "58-58 Western Plaguelands",
		items = {
			[1] = { str = "58-58 Western Plaguelands" },
			[2] = { str = "Go NE to the house at #C_[49,78]# and go upstairs. Turn in #Q_'Pamela's Doll'# accept #Q_'A Strange Historian'#.", x = 49, y = 78, zone = "Western Plaguelands" },
			[3] = { str = "Just north of the house at #C_[49,76]# you’ll see an off color gravestone. Loot it and get the wedding ring for #Q_'A Strange Historian'#.", x = 49, y = 76, zone = "Western Plaguelands" },
			[4] = { str = "Go in the west entrance to Andorhal at #C_[39,71]# and you should see the only full building, looks like an Inn at #C_[39,68]#. Go upstairs and turn in #Q_'A Strange Historian'# accept #Q_'The Annals of Darrowshire'# also accept #Q_'A Matter of Time'#.", x = 39, y = 68, zone = "Western Plaguelands" },
			[5] = { str = "Now look all around the city here for busted up silo’s with a blue light coming out of them. Use the silo horn to summon the worms, it spawns 1-3 mobs for #Q_'A Matter of Time'#. Once you kill the last one they have a tendency to spawn another. They’re more on the East side of town." },
			[6] = { str = "Go to the Town Hall near all the mobs in the middle of town, you can sneak in from the SW side. Grab the books on the floor until you get the #Q_'The Annals of Darrowshire'# Some will spawn mobs." },
			[7] = { str = "Go back to the Inn at #C_[39,68]# and to the top floor, turn in #Q_'A Matter of Time'# accept #Q_'Counting Out Time'# also turn in #Q_'The Annals of Darrowshire'# accept #Q_'Brother Carlin'#.", x = 39, y = 68, zone = "Western Plaguelands" },
			[8] = { str = "Go outside and look around the busted houses for little metal lunchboxes for #Q_'Counting Out Time'#." },
			[9] = { str = "Go back to the Inn at #C_[39,68]# and turn in #Q_'Counting Out Time'# you can now repeat this quest for a dampener. This is used vs the 61 elite.", x = 39, y = 68, zone = "Western Plaguelands" },
		}
	},

-----------58-58 Eastern Plaguelands
	[5858] = {
		title = "58-58 Eastern Plaguelands",
		items = {
			[1] = { str = "58-58 Eastern Plaguelands" },
			[2] = { str = "Run out of here and into #A_Eastern Plaguelands# up to the NW at #C_[7,43]# and turn in the 3 quests #Q_'Demon Dogs'# #Q_'Blood Tinged Skies'# and #Q_'Carrion Grubbage'# accept #Q_'Redemption'# then listen to him and turn it back in. Then accept #Q_'Of Forgotten Memories'#.", x = 7, y = 43, zone = "Eastern Plaguelands" },
			[3] = { str = "Go to the Undercroft at #C_[28,86]# and run behind it. Touch the dirt pile to summon Mercutio and 3 guards. Easiest way to do this is to kite him away from his buddies or get a group. Loot his body for the hammer for #Q_'Of Forgotten Memories'#.", x = 28, y = 86, zone = "Eastern Plaguelands" },
			[4] = { str = "Now go to the bottom of the crypt and kill the very big troll for #Q_'Zaeldarr the Outcast'# Only grab the scroll on the floor if u want." },
			[5] = { str = "Go back to #C_[7,43]# and turn in #Q_'Of Forgotten Memories'# accept #Q_'Of Lost Honor'#.", x = 7, y = 43, zone = "Eastern Plaguelands" },
			[6] = { str = "Either Run to Light’s Hope Chapel or back to Chillwind and fly there." },
			[7] = { str = "Turn in #Q_'Brother Carlin'# accept #Q_'Villains of Darrowshire'#." },
			[8] = { str = "Go down to #C_[79,63]# and turn in #Q_'Zaeldarr the Outcast'#.", x = 79, y = 63, zone = "Eastern Plaguelands" },
			[9] = { str = "Go down just North of Corin’s Crossing to #C_[53,65]# and grab the sword for #Q_'Villains of Darrowshire'# It’s down in the scar. Don’t forget to do some of #Q_'Defenders of Darrowshire'# at Corin’s Just get what you can.", x = 53, y = 65, zone = "Eastern Plaguelands" },
			[10] = { str = "Go north to Blackwood Lake at #C_[51,49]# and grab the skull under the water for #Q_'Villains of Darrowshire'#.", x = 51, y = 49, zone = "Eastern Plaguelands" },

			[11] = { str = "Go NE to around #C_[65,41]# and kill the zombies for part of #Q_'Defenders of Darrowshire'# there are quite a lot here you could probably finish.", x = 65, y = 41, zone = "Eastern Plaguelands" },
			[12] = { str = "Go to the lake at #C_[71,33]# and grab the flag next to the cage, it’s hard to see and is laying flat for #Q_'Of Lost Honor'#.", x = 71, y = 33, zone = "Eastern Plaguelands" },
			[13] = { str = "Go NW to the tower at #C_[56,24]# and turn in #Q_'Troubled Spirits of Kel'Theril# SKIP the next part.", x = 56, y = 24, zone = "Eastern Plaguelands" },
			[14] = { str = "Run West to #C_[45,34]# and loot the termite mounds here for #Q_'A Plague Upon Thee'# until you get 100 termites, also kill ghouls to finish up #Q_'Defenders of Darrowshire'#.", x = 45, y = 34, zone = "Eastern Plaguelands" },
			[15] = { str = "Run to #A_Lights Hope Chapel# (#A_Eastern Plaguelands#) now." },
			[16] = { str = "Go by the tent and turn in #Q_'Defenders of Darrowshire'# and #Q_'Villains of Darrowshire'#." },
			[17] = { str = "Fly to #A_Chillwind Point# (#A_Western Plaguelands#)." },
			[18] = { str = "Next to the house turn in #Q_'A Plague Upon Thee'#. Accept #Q_'A Plague Upon Thee'#(2)." },
		}
	},

------------58-59 Western Plaguelands
	[5859] = {
		title = "58-59 Western Plaguelands",
		items = {
			[1] = { str = "58-59 Western Plaguelands" },
			[2] = { str = "Run up to #C_[51,28]# and accept #Q_'Unfinished Business'#.", x = 52, y = 30, zone = "Western Plaguelands" },
			[3] = { str = "Start heading back SW around #C_[47,32]# and clear your way into the mill. Right click the box and place the termintes, then click the barrel and turn in #Q_'A Plague Upon Thee'#(2). Accept #Q_'A Plague Upon Thee'#(3).", x = 47, y = 32, zone = "Western Plaguelands" },
			[4] = { str = "Go just south of #C_[49,42]# and kill 2 knights and mages for #Q_'Unfinished Business'#then #C_[51,43]# for the hunters and medics.", x = 49, y = 42, zone = "Western Plaguelands" },
			[5] = { str = "Go to #C_[51,28]# and turn in #Q_'Unfinished Business'#. Accept #Q_'Unfinished Business'#(2).", x = 51, y = 28, zone = "Western Plaguelands" },
			[6] = { str = "Go to #C_[57,36]# and kill Huntsman Radly for #Q_'Unfinished Business'#(2) then north to the tower at #C_[55,23]# and kill Cavalier Durgen at the top.", x = 57, y = 36, zone = "Western Plaguelands" },
			[7] = { str = "Go straight out of the tower over the mountains to #C_[51,28]# and turn in #Q_'Unfinished Business'#(2) doing the 3rd part #Q_'Unfinished Business'#(3) is optional. Some classes can do it easier than others. You have to run north to hearthglen and run up into the big tower in the center of town at #C_[45,18]# and look over the edge until it says complete.", x = 45, y = 18, zone = "Western Plaguelands" },
			[8] = { str = "Run back straight south over the mountains to #C_[51,28]# and turn in #Q_'Unfinished Business'#(3).", x = 51, y = 28, zone = "Western Plaguelands" },
			[9] = { str = "Run into EPL and up to #C_[7,43]# and turn in #Q_'Of Lost Honor'# accept #Q_'Of Love and Family'#.", x = 7, y = 43, zone = "Eastern Plaguelands" },
			[10] = { str = "Go back to WPL and run/swim to the island Scholo is on at #C_[65,75]# and turn in #Q_'Of Love and Family'# SKIP the rest until you do strat.", x = 65, y = 75, zone = "Western Plaguelands" },

			[11] = { str = "Go back to Chillwind Camp at #C_[43,84]# and turn in #Q_'A Plague Upon Thee'#(3).", x = 43, y = 84, zone = "Western Plaguelands" },
			[12] = { str = "Now you want to do 2 things here. Get friendly with Argent Dawn and get 8-8.5k from leveling. You can do #Q_'Alas, Andorhal'# if you can find a group, it’s not that hard but can take 5-10 people. This will give you an easy 8300 xp instead of grinding." },
			[13] = { str = "Now you want to go up to andorhal and just grind until you’re friendly, once you’re friendly go back to Chillwind Camp and turn in all your scourge tokens. This allows you to buy mana biscuits which restore mana and health and more than food can. If you still need to level then go grind on mobs in andorhal again until you’re 8-8.5k from 59." },
			[14] = { str = "Once you’re 8k-8.5k from leveling, fly to Hinterlands and run all the way NE to the waterfall at #C_[80,46]# and jump off. Under the water loot the chest and finish #Q_'Cortello’s Riddle'#(3) I bet you thought I forgot about it.", x = 80, y = 46, zone = "The Hinterlands" },
			[15] = { str = "Use your #I_[Hearthstone]# to return to #A_Ironforge#." },
			[16] = { str = "Go to the throne room at #C_[43,52]# and accept #Q_'An Earnest Proposition'# (pick your class from the list) You have to have your classes tier 0 bracers in order to do this quest. You can buy it on AH.", x = 43, y = 52, zone = "Ironforge" },
			[17] = { str = "Fly to #A_Menethil Harbor# (#A_The Wetlands#) and boat to #A_Auberdine# (#A_Darkshore#), fly to #A_Darnassus#." },
			[18] = { str = "Go to #C_[35,8]# on the 2nd floor and turn in #Q_'Glyphed Oaken Branch'#.", x = 35, y = 8, zone = "Darnassus" },
			[19] = { str = "You should be 59, fly to #A_Everlook# (#A_Winterspring#)." },
		}
	},

------------59-60 Winterspring
	[5960] = {
		title = "59-60 Winterspring",
		items = {
			[1] = { str = "59-60 Winterspring" },
			[2] = { str = "On your left after entering turn in #Q_'Are We There, Yeti?'#(3)." },
			[3] = { str = "Near the back are 2 horde, accept #Q_'Luck Be With You'#." },
			[4] = { str = "Turn around and turn in #Q_'Felnok Steelspring'# accept #Q_'Chillwind Horns'#." },
			[5] = { str = "Do #Q_'An Earnest Proposition'# just kill the sabers until you have 15 blood." },
			[6] = { str = "Go just SE of here around #C_[55,18]# and kill the chimera’s for #Q_'Chillwind Horns'#.", x = 55, y = 18, zone = "Winterspring" },
			[7] = { str = "Run to Starfall Village at #C_[52,30]# and accept #Q_'Enraged Wildkin'# '.", x = 52, y = 30, zone = "Winterspring" },
			[8] = { str = "Turn in #Q_'Chillwind Horns'# SKIP the other unless you wanna go there." },
			[9] = { str = "Go south to #C_[59,59]# and touch the crate and turn in #Q_'Enraged Wildkin'#. Accept #Q_'Enraged Wildkin'#(2).", x = 59, y = 59, zone = "Winterspring" },
			[10] = { str = "Go back to the road at #C_[61,60]# and touch the wagon, turn in #Q_'Enraged Wildkin'#(2). Accept #Q_'Enraged Wildkin'#(3) grab the box in the snow.", x = 61, y = 60, zone = "Winterspring" },

			[11] = { str = "Run east again to #C_[63,59]# and turn in #Q_'Find Ranshalla'# accept #Q_'Guardians of the Altar'#.", x = 63, y = 59, zone = "Winterspring" },
			[12] = { str = "Now protect her while she does her thing. Make sure you get the feather off an owl for #Q_'Enraged Wildkin'#(3) When she stops in a cave, light the torch for her. If you don’t get the feather before you’re done keep killing until you got it." },
			[13] = { str = "Go south to #C_[62,69]# and loot the gems for #Q_'Luck Be With You'# hunters and locks can use pet to distract mobs while u loot, other classes will have to group.", x = 62, y = 69, zone = "Winterspring" },
			[14] = { str = "Use your #I_[Hearthstone]# to return to #A_Everlook# (#A_Winterspring#)." },
			[15] = { str = "Turn in #Q_'Luck Be With You'# at the 2 horde near the back of #A_Everlook# (#A_Winterspring#)." },
			[16] = { str = "You should be about 25% into 59 now." },
			[17] = { str = "Accept #Q_''Cache of Mau'Ari''# if you want to. This just gives you an item which allows the mobs here to drop stuff which you can turn in for buffs." },
			[18] = { str = "Go up to Starfall Village at #C_[52,30]# and turn in #Q_'Enraged Wildkin'#(3).", x = 52, y = 30, zone = "Winterspring" },
			[19] = { str = "Run to #C_[31,45]# and turn in #Q_'Mystery Goo'# accept #Q_'Toxic Horrors'#.", x = 31, y = 45, zone = "Winterspring" },
			[20] = { str = "Run into Felwood and go to #C_[48,24]# and kill toxic horrors for #Q_'Toxic Horrors'# these also drop essence of waters.", x = 48, y = 24, zone = "Felwood" },

			[21] = { str = "Fly to Rut’theran Village and go to the house at #C_[55,92]# and turn in #Q_'Guardians of the Altar'# accept #Q_'Wildkin of Elune'#.", x = 55, y = 92, zone = "Teldrassil" },
			[22] = { str = "Go in #A_Darnassus# and up to the top of the tower at #C_[34,8]# and turn in #Q_'Wildkin of Elune'#.", x = 34, y = 8, zone = "Darnassus" },
			[23] = { str = "Fly back to Felwood." },
			[24] = { str = "Run back up into Winterspring and over to #C_[31,45]# turn in #Q_'Toxic Horrors'# accept #Q_'Winterfall Runners'#.", x = 31, y = 45, zone = "Winterspring" },
			[25] = { str = "Now to find the runners. They travel anywhere from the Timbermaw Cave east to #C_[53,34]# then up around the mountains north of #A_Everlook# (#A_Winterspring#) over to Winterfall Village. If you didn’t see them from the cave to here, head east. You don’t have to kill all 3.", x = 53, y = 34, zone = "Winterspring" },
			[26] = { str = "Once you get the crate run back to #C_[31,45]# Turn in Winterfall Runners an ambush will come from the lake, if u don’t move from the tent they won’t aggro you. SKIP the next part unless you get a group.", x = 31, y = 45, zone = "Winterspring" },
			[27] = { str = "Use your #I_[Hearthstone]# to return to #A_Everlook# (#A_Winterspring#)." },
			[28] = { str = "You should be 45-50% through 59. Now I make sure I got my mana bisquits and I start grinding until im 6600 to 60. Anywhere is fine where there is a lot of mobs It’s all a matter of preference. I choose the owl wing thicket at #C_[64,62]# or anything north of #A_Everlook# (#A_Winterspring#). I like to kill the furlbogs for timbermaw rep also.", x = 64, y = 62, zone = "Winterspring" },
			[29] = { str = "If you don’t want to grind this large amount then do BRD, scholo, or strat. You have a lot of quests in those spots to do." },
			[30] = { str = "Once you’re 6600 to 60 go to #A_Ironforge# and turn in #Q_'An Earnest Proposition'#." },

			[31] = { str = "Grats on 60.fuck the police." },
		}
	},
}
