Table_003_Horde_12to20 = {
-----------12-15 Barrens
	[1215] = {
		title = "12-15 Barrens",
		items = {
			[1] = { str = "12-15 Barrens" },
			[2] = { str = "Turn in #TURNIN'Conscript of the Horde'# (#COORDS[62,19]# in the Barrens) accept #ACCEPT'Crossroads Conscription'#.", x = 62, y = 19, zone = "The Barrens" },
			[3] = { str = "Then turn in #TURNIN'Ak'Zeloth'# #COORDS[62,20]# I SKIP '#NPCThe Demon Seed'#.", x = 62, y = 20, zone = "The Barrens" },
			[4] = { str = "Run to XRs (Crossroads) at #COORDS[52,30]# #HUNTER (If you're a hunter do step 4)#", x = 52, y = 30, zone = "The Barrens" },
			[5] = { str = "#HUNTER04) While on my way to XRs, I  abandon my crab and tame a Savannah Huntress cat, for my new pet.#" },
			[6] = { str = "Once at XRs at #COORDS[52,30]# turn in #TURNIN'Crossroads Conscription'# Then accept all quests in XRs (which includes: #ACCEPT'Meats to Orgrimmar'# #ACCEPT'Plainstrider Menace'#  #ACCEPT'The Forgotten Pools'# #ACCEPT'Raptor Thieves'# #ACCEPT'Wharfmaster Dizzywig'# #ACCEPT'Fungal Spores'# #ACCEPT'Disrupt the Attacks'# #ACCEPT'Supplies for the Crossroads'# and #ACCEPT'Harpy Raiders'# on top of the watch tower). Turn in #TURNIN'Meats to Orgrimmar'# i SKIP '#NPCRide to Orgrimmar'#.", x = 52, y = 30, zone = "The Barrens" },
			[7] = { str = "Make XRs your home." },
			[8] = { str = "Get FP (Flight Path)" },
			[9] = { str = "Then go do: #DOQUEST'Disrupt the Attacks'# along with #DOQUEST'Plainstrider Menace'# and #DOQUEST'Raptor Thieves'# (all around #COORDS[54,26]#). NOTE: If you find #ACCEPT'Chen's Empty Keg'# accept it.", x = 54, y = 26, zone = "The Barrens" },
			[10] = { str = "Once #DOQUEST'Disrupt the Attacks'# is completed go turn it in at #COORDS[52,30]# and accept #ACCEPT'Supplies for the Crossroads'.", x = 52, y = 30, zone = "The Barrens" },

			[11] = { str = "Go do #DOQUEST'Supplies for the Crossroads'# at #COORDS[56,26]#, then run back to Thork and #ACCEPT'The Disruption Ends'#, you'll be going back up to the razormanes one more time.", x = 56, y = 26, zone = "The Barrens" },
			[12] = { str = "If you find #ACCEPT'Chen's Empty Keg'# accept it." },
			[13] = { str = "Once #DOQUEST'Plainstrider Menace'# & #DOQUEST'The Disruption Ends'# and are complete." },
			[14] = { str = "Go to Ratchet, get the Flight Path there #COORDS[63,37]# and accept #ACCEPT'Raptor Horns'# #ACCEPT'Samophlange'# #ACCEPT'Southsea Freebooters'#  #ACCEPT'The Guns of Northwatch'#. Turn in #TURNIN'Chen's Empty Keg'# if you have it. accept the next part to it, and focus on doing this quest while throughout the Barrens.", x = 63, y = 37, zone = "The Barrens" },
			[15] = { str = "Grab the quest #ACCEPT'WANTED: Baron Longshore'# (the wanted sign by the bank)" },
			[16] = { str = "Turn in #TURNIN'Wharfmaster Dizzywig'# (goblin on the docks) accept #ACCEPT'Miner's Fortune'#." },
			[17] = { str = "Then go do #DOQUEST'Southsea Freebooters'# along with #DOQUEST'WANTED: Baron Longshore'# (along the shore just south of Ratchet)" },
			[18] = { str = "Once those are done, turn them in at #COORDS[63,36]#, accept new ones, then go do:", x = 63, y = 36, zone = "The Barrens" },
			[19] = { str = "#TURNIN'The Missing Shipment'# (just turn this in at the goblin at the docks) accept #ACCEPT'The Missing Shipment'# part2" },
			[20] = { str = "Run back, to Gazlowe #COORDS[63,36]#, turn in #TURNIN'The Missing Shipment'# part2 accept #ACCEPT'Stolen Booty'#.", x = 63, y = 36, zone = "The Barrens" },

			[21] = { str = "Then go do #DOQUEST'Stolen Booty'# (south of Ratchet again) (Telescopic Lens=64.49, and Shipment of Boots=#COORDS[63,50]#)", x = 63, y = 50, zone = "The Barrens" },
			[22] = { str = "Once #DOQUEST'Stolen Booty'#  is completed hearth to XRs. (don't turn it in yet)" },
			[23] = { str = "Turn in #TURNIN'The Disruption Ends'# and #TURNIN'Supplies for the Crossroads'#." },
			[24] = { str = "Turn in #TURNIN'Plainstrider Menace'# (It should be done by now) accept #ACCEPT'The Zhevra'#. Also accept #ACCEPT'Consumed by Hatred'# and #ACCEPT'Lost in Battle'#." },
			[25] = { str = "At this point, I make sure I have three/four 6 slot bags, if I don't, I buy them at the bag vendor." },
			[26] = { str = "Run west from XRs, go to the guy in the hut. at #COORDS[45,28]#", x = 45, y = 28, zone = "The Barrens" },
			[27] = { str = "Accept: #ACCEPT'Kolkar Leaders'# and #ACCEPT'Centaur Bracers'#." },
			[28] = { str = "Then go start doing:" },
			[29] = { str = "#DOQUEST'Kolkar Leaders'# #DOQUEST'Centaur Bracers'# #DOQUEST'Raptor Thieves'# #DOQUEST'The Zhevra'# #DOQUEST'Fungal Spores'#  and #DOQUEST'The Forgotten Pools'# (all of these are done just slightly north of where you are)" },
			[30] = { str = "Once #DOQUEST'Kolkar Leaders'# is done. (Barak is at #COORDS[43,24]#).", x = 43, y = 24, zone = "The Barrens" },

			[31] = { str = "Go do: #DOQUEST'Harpy Raiders'# at #COORDS[38,17]#", x = 38, y = 17, zone = "The Barrens" },
			[32] = { str = "Once #DOQUEST'Harpy Raiders'# is done, grind your way down into Stonetalon Mountains (at 35.27 in the Barrens).", x = 35, y = 27, zone = "The Barrens" },
		}
	},

-----------15-16 Stonetalon Mountains
	[1516] = {
		title = "15-16 Stonetalon Mountains",
		items = {
			[1] = { str = "15-16 Stonetalon Mountains" },
			[2] = { str = "Accept #ACCEPT'Goblin Invaders'# (you should first accept #ACCEPT'Spirits of Stonetalon'# from Zor Lonetree in Orgrimmar, but this is a lvl13 quest. Grab it if you're in Orgrimmar at that level) and #ACCEPT'Avenge My Village'# (at 35.27 in the Barrens)", x = 35, y = 27, zone = "The Barrens" },
			[3] = { str = "Go do: #DOQUEST'Avenge My Village'# then turn it in." },
			[4] = { str = "Then do: #DOQUEST'Kill Grundig Darkcloud'# (he is at #COORDS[73,86]#)", x = 73, y = 86, zone = "Stonetalon Mountains" },
			[5] = { str = "NOTE: I usually skip the escort quest there (#DOQUEST'Protect Kaya'# 73.85 in the hut), This quest can be a little tough, kill the Sorcerer first that pops out during the one battle you have to fight. If it's too hard for you, you can skip it.", x = 73, y = 85, zone = "Stonetalon Mountains" },
			[6] = { str = "Turn in #TURNIN'Kill Grundig Darkcloud'# and #TURNIN'Protect Kaya'# if you did it (at 35.28 in the Barrens). accept #ACCEPT'Kaya's Alive'#.", x = 35, y = 28, zone = "The Barrens" },
			[7] = { str = "Then go back to the Barrens." },
		}
	},

-----------16-20 Barrens PART1
	[1618] = {
		title = "16-20 Barrens (part 1)",
		items = {
			[1] = { str = "16-20 Barrens Part 1" },
			[2] = { str = "Grind your way back to the guy in the hut at #COORDS[45,28]#", x = 45, y = 28, zone = "The Barrens" },
			[3] = { str = "Turn in #TURNIN'Kolkar Leaders'# accept #ACCEPT'Verog the Dervish'#. Also turn in #TURNIN'Centaur Bracers'# if it is done, if not don't worry." },
			[4] = { str = "Make sure you finish up #DOQUEST'Raptor Thieves'# #DOQUEST'The Zhevra'# #DOQUEST'Fungal Spores'# and #DOQUEST'The Forgotten Pools'# before returning to the XRs." },
			[5] = { str = "Run to XRs (Cross Roads at #COORDS[52,30]#)", x = 52, y = 30, zone = "The Barrens" },
			[6] = { str = "Turn in ALL quests, grab ALL new ones." },
			[7] = { str = "then do #ACCEPT'Apothecary Zamah'# Which has you run all the way to Thunder Bluff (TB) at the spirt rise cave. Do these along the way:" },
			[8] = { str = "Grind your way down south (to #COORDS[49,50]#) and do #DOQUEST'Lost in Battle'# (Manrik's Wife) She's laying dead by the hut, west of the bridge.", x = 49, y = 50, zone = "The Barrens" },
			[9] = { str = "Go down to Camp Taurajo (CT) accept #ACCEPT'Tribes at War'# (gnoll in the cage at #COORDS[44,59]#), and get FP there.", x = 44, y = 59, zone = "The Barrens" },
			[10] = { str = "Then do #DOQUEST'Apothecary Zamah'# Which has you run all the way to Thunder Bluff (TB) (in Mulgore at #COORDS[39,27]#).", x = 39, y = 27, zone = "Mulgore" },

			[11] = { str = "Once at TB, go to #NPCweapon master# #COORDS[40,62]#, #HUNTERget Guns and Staff skills. (I do this as a Hunter),# get what weapon skills you need for your class)" },
			[12] = { str = "Talk to your #NPCClass Trainer# to learn new spells and abilities." },
			[13] = { str = "Turn in #TURNIN'Apothecary Zamah'# the cave below the spirit rise at #COORDS[29,29]#.", x = 29, y = 29, zone = "The Barrens" },
			[14] = { str = "Go to first aid guy at spirit rise to build up first aid." },
			[15] = { str = "Go up to the tower in middle of town to get FP, but DON'T fly back to XRs." },
			[16] = { str = "Hearth back to XRs." },
			[17] = { str = "Turn in #TURNIN'Lost in Battle'#." },
			[18] = { str = "Then go north west of XRs and do #DOQUEST'Prowlers of the Barrens'# #COORDS[37,20]#", x = 37, y = 20, zone = "The Barrens" },
			[19] = { str = "Then #DOQUEST'Harpy Lieutenants'# #COORDS[38,14]#", x = 38, y = 14, zone = "The Barrens" },
			[20] = { str = "Then grind your way east and do #DOQUEST'Samophlange'# #COORDS[52,11]# complete the whole chain, which in the end has you kill the goblin for the key." },

			[21] = { str = "Then go east to Sludge Fen and do #DOQUEST'Ignition'# #COORDS[56,8]#", x = 56, y = 8, zone = "The Barrens" },
			[22] = { str = "Then do #DOQUEST'The Escape'# (#VIDEOSee video on how I do Ignition and Escape#)" },
			[23] = { str = "Then go north-east and do #DOQUEST'Miner's Fortune'# #COORDS[61,5]#", x = 61, y = 5, zone = "The Barrens" },
			[24] = { str = "Then grind your way south to Ratchet #COORDS[63,37]#.", x = 63, y = 37, zone = "The Barrens" },
			[25] = { str = "Turn in #TURNIN'Stolen Booty'#  #TURNIN'Samophlange'# #TURNIN'The Escape'#. accept #ACCEPT'Ziz Fizziks'#. and SKIP '#NPCWenikee Boltbucket'#." },
			[26] = { str = "Turn in #TURNIN'Miner's Fortune'#." },
			[27] = { str = "Then go west of ratchet and do #DOQUEST'The Stagnant Oasis'# and #DOQUEST'Verog the Dervish'# at #COORDS[54,43]#", x = 54, y = 43, zone = "The Barrens" },
			[28] = { str = "Then run to XRs #COORDS[52,30]#.", x = 52, y = 30, zone = "The Barrens" },
			[29] = { str = "Turn in #TURNIN'Prowlers of the Barrens'# #TURNIN'Harpy Lieutenants'# and #TURNIN'The Stagnant Oasis'#. accept #ACCEPT'Altered Beings'# #ACCEPT'Echeyakee'# #ACCEPT'Serena Bloodfeather'# #ACCEPT'Report to Kadrak'# and #ACCEPT'Egg Hunt'#." },
			[30] = { str = "Go west of XRs #COORDS[45,28]#, to turn in: #TURNIN'Centaur Bracers'# and #TURNIN'Verog the Dervish'#. I SKIP '#NPCHezrul Bloodmark'#.", x = 45, y = 28, zone = "The Barrens" },

			[31] = { str = "Then grind your way north and do: #DOQUEST'Serena Bloodfeather'# at #COORDS[38,11]#", x = 38, y = 11, zone = "The Barrens" },
			[32] = { str = "Then go east (grind mobs along the way) and do #DOQUEST'Echeyakee'# (coords are at #COORDS[55,17]#).", x = 55, y = 17, zone = "The Barrens" },
			[33] = { str = "Hearth to XRs." },
			[34] = { str = "Turn in #TURNIN'Echeyakee'#. accept #ACCEPT'The Angry Scytheclaws'#." },
			[35] = { str = "Turn in #TURNIN'Serena Bloodfeather'# accept #ACCEPT'Letter to Jin'Zil'#. Make sure that #ACCEPT'Consumed by Hatred'# is also accepted." },
		}
	},

-----------16-20 Barrens PART2
	[1820] = {
		title = "16-20 Barrens (part 2)",
		items = {
			[1] = { str = "16-20 Barrens Part 2" },
			[2] = { str = "Go down south and do:" },
			[3] = { str = "#DOQUEST'Altered Beings'#  #COORDS[55,42]#", x = 55, y = 42, zone = "The Barrens" },
			[4] = { str = "#DOQUEST'Raptor Horns'# along with #DOQUEST'Stolen Silver'#  at #COORDS[57,54]#.", x = 57, y = 54, zone = "The Barrens" },
			[5] = { str = "#DOQUEST'The Angry Scytheclaws'#  #COORDS[51,46]#", x = 51, y = 46, zone = "The Barrens" },
			[6] = { str = "#DOQUEST'Tribes at War'# along with #DOQUEST'Consumed by Hatred'#  at #COORDS[51,54]#", x = 51, y = 54, zone = "The Barrens" },
			[7] = { str = "Then grind your way to Camp Taurajo #COORDS[45,58]#", x = 45, y = 58, zone = "The Barrens" },
			[8] = { str = "Accept #ACCEPT'Weapons of Choice'#." },
			[9] = { str = "Go to gnoll in the cage." },
			[10] = { str = "Turn in #TURNIN'Tribes at War'# accept #ACCEPT'Blood Shards of Agamaggan'# and #ACCEPT'Betrayal from Within'#." },

			[11] = { str = "Turn in #TURNIN'Blood Shards of Agamaggan'#." },
			[12] = { str = "Turn in 10 bloodshards for #TURNIN'Spirit of the Wind'# (repeatable quest) for fast run speed." },
			[13] = { str = "At this point, you should be about half way to level 20." },
			[14] = { str = "Now comes the fun part: #NPCWailing Caverns# Instance! at #COORDS[46,36]#. You will need a full group to do this. OPTIONAL: GRIND all the way to level 20! (which is what I do, but it's not recommended). I grind away at beasts and brisstlebacks just north of CT." },
			[15] = { str = "Keep an eye out for the mobs named #NPCLakota'mani# (a big grey kodo around #COORDS[45,53]#) and #NPCOwatanka#. They drops #NPCHoof of Lakota'mani# which starts #DOQUEST'Lakota'mani'# and #NPCOwatanka's Tailspike# which starts #DOQUEST'Owatanka'#. Turn these in at CT when you get the chance." },
			[16] = { str = "Once you are at least 5 bars away from 20, run to XRs." },
			[17] = { str = "Turn in #TURNIN'Stolen Silver'# #TURNIN'Consumed by Hatred'# #TURNIN'Altered Beings'# and #TURNIN'The Angry Scytheclaws'#.accept all new quests. SKIP '#NPCMura Runetotem'#. You should be lvl 20 now." },
			[18] = { str = "Fly to Orgrimmar and talk to your #NPCClass Trainer# to learn new spells and abilities." },
			[19] = { str = "Grab #ACCEPT'The Ashenvale Hunt'# quest" },
			[20] = { str = "Hearth back to XRs." },

			[21] = { str = "Fly to Ratchet." },
			[22] = { str = "Turn in #TURNIN'Chen's Empty Keg'# (if you did it, if not don't worry). accept the next part to it." },
			[23] = { str = "Turn in #TURNIN'Raptor Horns'# accept #ACCEPT'Deepmoss Spider Eggs'# and #ACCEPT'Ziz Fizziks'#." },
			[24] = { str = "Then go do: #DOQUEST'The Guns of Northwatch'# at #COORDS[60,55]# (#VIDEOSee complete video on this quest along with the escort quest#)", x = 60, y = 55, zone = "The Barrens" },
			[25] = { str = "Then once #DOQUEST'The Guns of Northwatch'# is done, do:" },
			[26] = { str = "#DOQUEST'Free From the Hold'# (escort quest)" },
			[27] = { str = "Turn in both quests at Ratchet, then." },
			[28] = { str = "Hearth to XRs. (if hearth stone is still on a cooldown, fly to XRs)" },
			[29] = { str = "Run west into Stonetalon Mountains." },
		}
	},
}
