Table_003_Horde_12to20 = {
-----------12-15 Barrens
	[1215] = {
		title = "12-15 Barrens",
		items = {
			[1] = { str = "12-15 Barrens" },
			[2] = { str = "Turn in #QUEST'Conscript of the Horde'# (#COORDS[62,19]# in the Barrens) accept #QUEST'Crossroads Conscription'#.", x = 62, y = 19, zone = "The Barrens" },
			[3] = { str = "Then turn in #QUEST'Ak'Zeloth'# #COORDS[62,20]# I SKIP '#NPCThe Demon Seed'#.", x = 62, y = 20, zone = "The Barrens" },
			[4] = { str = "Run to XRs (Crossroads) at #COORDS[52,30]# #HUNTER (If you're a hunter do step 4)#.", x = 52, y = 30, zone = "The Barrens" },
			[5] = { str = "#HUNTER04) While on my way to XRs, I  abandon my crab and tame a Savannah Huntress cat, for my new pet.#." },
			[6] = { str = "Once at XRs at #COORDS[52,30]# turn in #QUEST'Crossroads Conscription'# Then accept all quests in XRs (which includes: #QUEST'Meats to Orgrimmar'# #QUEST'Plainstrider Menace'#  #QUEST'The Forgotten Pools'# #QUEST'Raptor Thieves'# #QUEST'Wharfmaster Dizzywig'# #QUEST'Fungal Spores'# #QUEST'Disrupt the Attacks'# #QUEST'Supplies for the Crossroads'# and #QUEST'Harpy Raiders'# on top of the watch tower). Turn in #QUEST'Meats to Orgrimmar'# i SKIP '#NPCRide to Orgrimmar'#.", x = 52, y = 30, zone = "The Barrens" },
			[7] = { str = "Make XRs your home." },
			[8] = { str = "Get FP (Flight Path)." },
			[9] = { str = "Then go do: #QUEST'Disrupt the Attacks'# along with #QUEST'Plainstrider Menace'# and #QUEST'Raptor Thieves'# (all around #COORDS[54,26]#). NOTE: If you find #QUEST'Chen's Empty Keg'# accept it.", x = 54, y = 26, zone = "The Barrens" },
			[10] = { str = "Once #QUEST'Disrupt the Attacks'# is completed go turn it in at #COORDS[52,30]# and accept #QUEST'Supplies for the Crossroads'.", x = 52, y = 30, zone = "The Barrens" },

			[11] = { str = "Go do #QUEST'Supplies for the Crossroads'# at #COORDS[56,26]#, then run back to Thork and #QUEST'The Disruption Ends'#, you'll be going back up to the razormanes one more time.", x = 56, y = 26, zone = "The Barrens" },
			[12] = { str = "If you find #QUEST'Chen's Empty Keg'# accept it." },
			[13] = { str = "Once #QUEST'Plainstrider Menace'# & #QUEST'The Disruption Ends'# and are complete." },
			[14] = { str = "Go to Ratchet, get the Flight Path there #COORDS[63,37]# and accept #QUEST'Raptor Horns'# #QUEST'Samophlange'# #QUEST'Southsea Freebooters'#  #QUEST'The Guns of Northwatch'#. Turn in #QUEST'Chen's Empty Keg'# if you have it. accept the next part to it, and focus on doing this quest while throughout the Barrens.", x = 63, y = 37, zone = "The Barrens" },
			[15] = { str = "Grab the quest #QUEST'WANTED: Baron Longshore'# (the wanted sign by the bank)." },
			[16] = { str = "Turn in #QUEST'Wharfmaster Dizzywig'# (goblin on the docks) accept #QUEST'Miner's Fortune'#." },
			[17] = { str = "Then go do #QUEST'Southsea Freebooters'# along with #QUEST'WANTED: Baron Longshore'# (along the shore just south of Ratchet)." },
			[18] = { str = "Once those are done, turn them in at #COORDS[63,36]#, accept new ones, then go do:", x = 63, y = 36, zone = "The Barrens" },
			[19] = { str = "#QUEST'The Missing Shipment'# (just turn this in at the goblin at the docks) accept #QUEST'The Missing Shipment'# part2." },
			[20] = { str = "Run back, to Gazlowe #COORDS[63,36]#, turn in #QUEST'The Missing Shipment'# part2 accept #QUEST'Stolen Booty'#.", x = 63, y = 36, zone = "The Barrens" },

			[21] = { str = "Then go do #QUEST'Stolen Booty'# (south of Ratchet again) (Telescopic Lens=64.49, and Shipment of Boots=#COORDS[63,50]#).", x = 63, y = 50, zone = "The Barrens" },
			[22] = { str = "Once #QUEST'Stolen Booty'#  is completed hearth to XRs. (don't turn it in yet)." },
			[23] = { str = "Turn in #QUEST'The Disruption Ends'# and #QUEST'Supplies for the Crossroads'#." },
			[24] = { str = "Turn in #QUEST'Plainstrider Menace'# (It should be done by now) accept #QUEST'The Zhevra'#. Also accept #QUEST'Consumed by Hatred'# and #QUEST'Lost in Battle'#." },
			[25] = { str = "At this point, I make sure I have three/four 6 slot bags, if I don't, I buy them at the bag vendor." },
			[26] = { str = "Run west from XRs, go to the guy in the hut. at #COORDS[45,28]#.", x = 45, y = 28, zone = "The Barrens" },
			[27] = { str = "Accept: #QUEST'Kolkar Leaders'# and #QUEST'Centaur Bracers'#." },
			[28] = { str = "Then go start doing:" },
			[29] = { str = "#QUEST'Kolkar Leaders'# #QUEST'Centaur Bracers'# #QUEST'Raptor Thieves'# #QUEST'The Zhevra'# #QUEST'Fungal Spores'#  and #QUEST'The Forgotten Pools'# (all of these are done just slightly north of where you are)." },
			[30] = { str = "Once #QUEST'Kolkar Leaders'# is done. (Barak is at #COORDS[43,24]#).", x = 43, y = 24, zone = "The Barrens" },

			[31] = { str = "Go do: #QUEST'Harpy Raiders'# at #COORDS[38,17]#.", x = 38, y = 17, zone = "The Barrens" },
			[32] = { str = "Once #QUEST'Harpy Raiders'# is done, grind your way down into Stonetalon Mountains (at 35.27 in the Barrens).", x = 35, y = 27, zone = "The Barrens" },
		}
	},

-----------15-16 Stonetalon Mountains
	[1516] = {
		title = "15-16 Stonetalon Mountains",
		items = {
			[1] = { str = "15-16 Stonetalon Mountains" },
			[2] = { str = "Accept #QUEST'Goblin Invaders'# (you should first accept #QUEST'Spirits of Stonetalon'# from Zor Lonetree in Orgrimmar, but this is a lvl13 quest. Grab it if you're in Orgrimmar at that level) and #QUEST'Avenge My Village'# (at 35.27 in the Barrens).", x = 35, y = 27, zone = "The Barrens" },
			[3] = { str = "Go do: #QUEST'Avenge My Village'# then turn it in." },
			[4] = { str = "Then do: #QUEST'Kill Grundig Darkcloud'# (he is at #COORDS[73,86]#).", x = 73, y = 86, zone = "Stonetalon Mountains" },
			[5] = { str = "NOTE: I usually skip the escort quest there (#QUEST'Protect Kaya'# 73.85 in the hut), This quest can be a little tough, kill the Sorcerer first that pops out during the one battle you have to fight. If it's too hard for you, you can skip it.", x = 73, y = 85, zone = "Stonetalon Mountains" },
			[6] = { str = "Turn in #QUEST'Kill Grundig Darkcloud'# and #QUEST'Protect Kaya'# if you did it (at 35.28 in the Barrens). accept #QUEST'Kaya's Alive'#.", x = 35, y = 28, zone = "The Barrens" },
			[7] = { str = "Then go back to the Barrens." },
		}
	},

-----------16-20 Barrens PART1
	[1618] = {
		title = "16-20 Barrens (part 1)",
		items = {
			[1] = { str = "16-20 Barrens Part 1" },
			[2] = { str = "Grind your way back to the guy in the hut at #COORDS[45,28]#.", x = 45, y = 28, zone = "The Barrens" },
			[3] = { str = "Turn in #QUEST'Kolkar Leaders'# accept #QUEST'Verog the Dervish'#. Also turn in #QUEST'Centaur Bracers'# if it is done, if not don't worry." },
			[4] = { str = "Make sure you finish up #QUEST'Raptor Thieves'# #QUEST'The Zhevra'# #QUEST'Fungal Spores'# and #QUEST'The Forgotten Pools'# before returning to the XRs." },
			[5] = { str = "Run to XRs (Cross Roads at #COORDS[52,30]#).", x = 52, y = 30, zone = "The Barrens" },
			[6] = { str = "Turn in ALL quests, grab ALL new ones." },
			[7] = { str = "then do #QUEST'Apothecary Zamah'# Which has you run all the way to Thunder Bluff (TB) at the spirt rise cave. Do these along the way:" },
			[8] = { str = "Grind your way down south (to #COORDS[49,50]#) and do #QUEST'Lost in Battle'# (Manrik's Wife) She's laying dead by the hut, west of the bridge.", x = 49, y = 50, zone = "The Barrens" },
			[9] = { str = "Go down to Camp Taurajo (CT) accept #QUEST'Tribes at War'# (gnoll in the cage at #COORDS[44,59]#), and get FP there.", x = 44, y = 59, zone = "The Barrens" },
			[10] = { str = "Then do #QUEST'Apothecary Zamah'# Which has you run all the way to Thunder Bluff (TB) (in Mulgore at #COORDS[39,27]#).", x = 39, y = 27, zone = "Mulgore" },

			[11] = { str = "Once at TB, go to #NPCweapon master# #COORDS[40,62]#, #HUNTERget Guns and Staff skills. (I do this as a Hunter),# get what weapon skills you need for your class)." },
			[12] = { str = "Talk to your #NPCClass Trainer# to learn new spells and abilities." },
			[13] = { str = "Turn in #QUEST'Apothecary Zamah'# the cave below the spirit rise at #COORDS[29,29]#.", x = 29, y = 29, zone = "The Barrens" },
			[14] = { str = "Go to first aid guy at spirit rise to build up first aid." },
			[15] = { str = "Go up to the tower in middle of town to get FP, but DON'T fly back to XRs." },
			[16] = { str = "Hearth back to XRs." },
			[17] = { str = "Turn in #QUEST'Lost in Battle'#." },
			[18] = { str = "Then go north west of XRs and do #QUEST'Prowlers of the Barrens'# #COORDS[37,20]#.", x = 37, y = 20, zone = "The Barrens" },
			[19] = { str = "Then #QUEST'Harpy Lieutenants'# #COORDS[38,14]#.", x = 38, y = 14, zone = "The Barrens" },
			[20] = { str = "Then grind your way east and do #QUEST'Samophlange'# #COORDS[52,11]# complete the whole chain, which in the end has you kill the goblin for the key." },

			[21] = { str = "Then go east to Sludge Fen and do #QUEST'Ignition'# #COORDS[56,8]#.", x = 56, y = 8, zone = "The Barrens" },
			[22] = { str = "Then do #QUEST'The Escape'# (#VIDEOSee video on how I do Ignition and Escape#)." },
			[23] = { str = "Then go north-east and do #QUEST'Miner's Fortune'# #COORDS[61,5]#.", x = 61, y = 5, zone = "The Barrens" },
			[24] = { str = "Then grind your way south to Ratchet #COORDS[63,37]#.", x = 63, y = 37, zone = "The Barrens" },
			[25] = { str = "Turn in #QUEST'Stolen Booty'#  #QUEST'Samophlange'# #QUEST'The Escape'#. accept #QUEST'Ziz Fizziks'#. and SKIP '#NPCWenikee Boltbucket'#." },
			[26] = { str = "Turn in #QUEST'Miner's Fortune'#." },
			[27] = { str = "Then go west of ratchet and do #QUEST'The Stagnant Oasis'# and #QUEST'Verog the Dervish'# at #COORDS[54,43]#.", x = 54, y = 43, zone = "The Barrens" },
			[28] = { str = "Then run to XRs #COORDS[52,30]#.", x = 52, y = 30, zone = "The Barrens" },
			[29] = { str = "Turn in #QUEST'Prowlers of the Barrens'# #QUEST'Harpy Lieutenants'# and #QUEST'The Stagnant Oasis'#. accept #QUEST'Altered Beings'# #QUEST'Echeyakee'# #QUEST'Serena Bloodfeather'# #QUEST'Report to Kadrak'# and #QUEST'Egg Hunt'#." },
			[30] = { str = "Go west of XRs #COORDS[45,28]#, to turn in: #QUEST'Centaur Bracers'# and #QUEST'Verog the Dervish'#. I SKIP '#NPCHezrul Bloodmark'#.", x = 45, y = 28, zone = "The Barrens" },

			[31] = { str = "Then grind your way north and do: #QUEST'Serena Bloodfeather'# at #COORDS[38,11]#.", x = 38, y = 11, zone = "The Barrens" },
			[32] = { str = "Then go east (grind mobs along the way) and do #QUEST'Echeyakee'# (coords are at #COORDS[55,17]#).", x = 55, y = 17, zone = "The Barrens" },
			[33] = { str = "Hearth to XRs." },
			[34] = { str = "Turn in #QUEST'Echeyakee'#. accept #QUEST'The Angry Scytheclaws'#." },
			[35] = { str = "Turn in #QUEST'Serena Bloodfeather'# accept #QUEST'Letter to Jin'Zil'#. Make sure that #QUEST'Consumed by Hatred'# is also accepted." },
		}
	},

-----------16-20 Barrens PART2
	[1820] = {
		title = "16-20 Barrens (part 2)",
		items = {
			[1] = { str = "16-20 Barrens Part 2" },
			[2] = { str = "Go down south and do:" },
			[3] = { str = "#QUEST'Altered Beings'#  #COORDS[55,42]#.", x = 55, y = 42, zone = "The Barrens" },
			[4] = { str = "#QUEST'Raptor Horns'# along with #QUEST'Stolen Silver'#  at #COORDS[57,54]#.", x = 57, y = 54, zone = "The Barrens" },
			[5] = { str = "#QUEST'The Angry Scytheclaws'#  #COORDS[51,46]#.", x = 51, y = 46, zone = "The Barrens" },
			[6] = { str = "#QUEST'Tribes at War'# along with #QUEST'Consumed by Hatred'#  at #COORDS[51,54]#.", x = 51, y = 54, zone = "The Barrens" },
			[7] = { str = "Then grind your way to Camp Taurajo #COORDS[45,58]#.", x = 45, y = 58, zone = "The Barrens" },
			[8] = { str = "Accept #QUEST'Weapons of Choice'#." },
			[9] = { str = "Go to gnoll in the cage." },
			[10] = { str = "Turn in #QUEST'Tribes at War'# accept #QUEST'Blood Shards of Agamaggan'# and #QUEST'Betrayal from Within'#." },

			[11] = { str = "Turn in #QUEST'Blood Shards of Agamaggan'#." },
			[12] = { str = "Turn in 10 bloodshards for #QUEST'Spirit of the Wind'# (repeatable quest) for fast run speed." },
			[13] = { str = "At this point, you should be about half way to level 20." },
			[14] = { str = "Now comes the fun part: #NPCWailing Caverns# Instance! at #COORDS[46,36]#. You will need a full group to do this. OPTIONAL: GRIND all the way to level 20! (which is what I do, but it's not recommended). I grind away at beasts and brisstlebacks just north of CT." },
			[15] = { str = "Keep an eye out for the mobs named #NPCLakota'mani# (a big grey kodo around #COORDS[45,53]#) and #NPCOwatanka#. They drops #NPCHoof of Lakota'mani# which starts #QUEST'Lakota'mani'# and #NPCOwatanka's Tailspike# which starts #QUEST'Owatanka'#. Turn these in at CT when you get the chance." },
			[16] = { str = "Once you are at least 5 bars away from 20, run to XRs." },
			[17] = { str = "Turn in #QUEST'Stolen Silver'# #QUEST'Consumed by Hatred'# #QUEST'Altered Beings'# and #QUEST'The Angry Scytheclaws'#.accept all new quests. SKIP '#NPCMura Runetotem'#. You should be lvl 20 now." },
			[18] = { str = "Fly to Orgrimmar and talk to your #NPCClass Trainer# to learn new spells and abilities." },
			[19] = { str = "Grab #QUEST'The Ashenvale Hunt'# quest." },
			[20] = { str = "Hearth back to XRs." },

			[21] = { str = "Fly to Ratchet." },
			[22] = { str = "Turn in #QUEST'Chen's Empty Keg'# (if you did it, if not don't worry). accept the next part to it." },
			[23] = { str = "Turn in #QUEST'Raptor Horns'# accept #QUEST'Deepmoss Spider Eggs'# and #QUEST'Ziz Fizziks'#." },
			[24] = { str = "Then go do: #QUEST'The Guns of Northwatch'# at #COORDS[60,55]# (#VIDEOSee complete video on this quest along with the escort quest#).", x = 60, y = 55, zone = "The Barrens" },
			[25] = { str = "Then once #QUEST'The Guns of Northwatch'# is done, do:" },
			[26] = { str = "#QUEST'Free From the Hold'# (escort quest)." },
			[27] = { str = "Turn in both quests at Ratchet, then." },
			[28] = { str = "Hearth to XRs. (if hearth stone is still on a cooldown, fly to XRs)." },
			[29] = { str = "Run west into Stonetalon Mountains." },
		}
	},
}
