Table_003_Horde_12to20 = {
-----------12-15 Barrens
	[1215] = {
		title = "12-15 Barrens",
		items = {
			[1] = { str = "12-15 Barrens" },
			[2] = { str = "Turn in #Q_'Conscript of the Horde'# (#C_[62,19]# in the Barrens) accept #Q_'Crossroads Conscription'#.", x = 62, y = 19, zone = "The Barrens" },
			[3] = { str = "Then turn in #Q_'Ak'Zeloth'# #C_[62,20]# I SKIP '#N_The Demon Seed'#.", x = 62, y = 20, zone = "The Barrens" },
			[4] = { str = "Run to #A_Crossroads# (#A_The Barrens#) at #C_[52,30]# #HUNTER (If you're a hunter do step 4)#.", x = 52, y = 30, zone = "The Barrens" },
			[5] = { str = "#HUNTER04) While on my way to #A_Crossroads# (#A_The Barrens#), I  abandon my crab and tame a Savannah Huntress cat, for my new pet.#." },
			[6] = { str = "Once at #A_Crossroads# (#A_The Barrens#) at #C_[52,30]# turn in #Q_'Crossroads Conscription'# Then accept all quests in #A_Crossroads# (#A_The Barrens#) (which includes: #Q_'Meats to Orgrimmar'# #Q_'Plainstrider Menace'# #Q_'The Forgotten Pools'# #Q_'Raptor Thieves'# #Q_'Wharfmaster Dizzywig'# #Q_'Fungal Spores'# #Q_'Disrupt the Attacks'# #Q_'Supplies for the Crossroads'# and #Q_'Harpy Raiders'# on top of the watch tower). Turn in #Q_'Meats to Orgrimmar'# i SKIP '#N_Ride to Orgrimmar'#.", x = 52, y = 30, zone = "The Barrens" },
			[7] = { str = "Talk to the #N_Innkeeper# to make #A_Crossroads# (#A_The Barrens#) your home." },
			[8] = { str = "Unlock the flight path by talking to the #N_Flight Master#." },
			[9] = { str = "Then go do: #Q_'Disrupt the Attacks'# along with #Q_'Plainstrider Menace'# and #Q_'Raptor Thieves'# (all around #C_[54,26]#). NOTE: If you find #Q_'Chen's Empty Keg'# accept it.", x = 54, y = 26, zone = "The Barrens" },
			[10] = { str = "Once #Q_'Disrupt the Attacks'# is completed go turn it in at #C_[52,30]# and accept #Q_'Supplies for the Crossroads'.", x = 52, y = 30, zone = "The Barrens" },

			[11] = { str = "Go do #Q_'Supplies for the Crossroads'# at #C_[56,26]#, then run back to Thork and #Q_'The Disruption Ends'#, you'll be going back up to the razormanes one more time.", x = 56, y = 26, zone = "The Barrens" },
			[12] = { str = "If you find #Q_'Chen's Empty Keg'# accept it." },
			[13] = { str = "Once #Q_'Plainstrider Menace'# & #Q_'The Disruption Ends'# and are complete." },
			[14] = { str = "Go to #A_Ratchet# (#A_The Barrens#). Unlock the flight path by talking to the #N_Flight Master# #C_[63,37]# and accept #Q_'Raptor Horns'# #Q_'Samophlange'# #Q_'Southsea Freebooters'#  #Q_'The Guns of Northwatch'#. Turn in #Q_'Chen's Empty Keg'# if you have it. Accept the next part to it, and focus on doing this quest while throughout the Barrens.", x = 63, y = 37, zone = "The Barrens" },
			[15] = { str = "Grab the quest #Q_'WANTED: Baron Longshore'# (the wanted sign by the bank)." },
			[16] = { str = "Turn in #Q_'Wharfmaster Dizzywig'# (goblin on the docks) accept #Q_'Miner's Fortune'#." },
			[17] = { str = "Then go do #Q_'Southsea Freebooters'# along with #Q_'WANTED: Baron Longshore'# (along the shore just south of #A_Ratchet# (#A_The Barrens#))." },
			[18] = { str = "Once those are done, turn them in at #C_[63,36]#, accept new ones, then go do:", x = 63, y = 36, zone = "The Barrens" },
			[19] = { str = "#Q_'The Missing Shipment'# (just turn this in at the goblin at the docks) accept #Q_'The Missing Shipment'#(2)." },
			[20] = { str = "Run back, to Gazlowe #C_[63,36]#, turn in #Q_'The Missing Shipment'#(2) accept #Q_'Stolen Booty'#.", x = 63, y = 36, zone = "The Barrens" },

			[21] = { str = "Then go do #Q_'Stolen Booty'# (south of #A_Ratchet# (#A_The Barrens#) again) (Telescopic Lens=#C_[64,49]#, and Shipment of Boots=#C_[63,50]#).", x = 63, y = 50, zone = "The Barrens" },
			[22] = { str = "Once #Q_'Stolen Booty'#  is completed Use your #I_[Hearthstone]# to return to #A_Crossroads# (#A_The Barrens#). (don't turn it in yet)." },
			[23] = { str = "Turn in #Q_'The Disruption Ends'# and #Q_'Supplies for the Crossroads'#." },
			[24] = { str = "Turn in #Q_'Plainstrider Menace'# (It should be done by now) accept #Q_'The Zhevra'#. Also accept #Q_'Consumed by Hatred'# and #Q_'Lost in Battle'#." },
			[25] = { str = "At this point, I make sure I have three/four 6 slot bags, if I don't, I buy them at the bag vendor." },
			[26] = { str = "Run west from #A_Crossroads# (#A_The Barrens#), go to the guy in the hut. At #C_[45,28]#.", x = 45, y = 28, zone = "The Barrens" },
			[27] = { str = "Accept: #Q_'Kolkar Leaders'# and #Q_'Centaur Bracers'#." },
			[28] = { str = "Then go start doing:" },
			[29] = { str = "#Q_'Kolkar Leaders'# #Q_'Centaur Bracers'# #Q_'Raptor Thieves'# #Q_'The Zhevra'# #Q_'Fungal Spores'#  and #Q_'The Forgotten Pools'# (all of these are done just slightly north of where you are)." },
			[30] = { str = "Once #Q_'Kolkar Leaders'# is done. (Barak is at #C_[43,24]#).", x = 43, y = 24, zone = "The Barrens" },

			[31] = { str = "Go do: #Q_'Harpy Raiders'# at #C_[38,17]#.", x = 38, y = 17, zone = "The Barrens" },
			[32] = { str = "Once #Q_'Harpy Raiders'# is done, grind your way down into #A_Stonetalon Mountains# (at #C_[35,27]# in the Barrens).", x = 35, y = 27, zone = "The Barrens" },
		}
	},

-----------15-16 Stonetalon Mountains
	[1516] = {
		title = "15-16 Stonetalon Mountains",
		items = {
			[1] = { str = "15-16 Stonetalon Mountains" },
			[2] = { str = "Accept #Q_'Goblin Invaders'# (you should first accept #Q_'Spirits of Stonetalon'# from Zor Lonetree in #A_Orgrimmar#, but this is a lvl13 quest. Grab it if you're in #A_Orgrimmar# at that level) and #Q_'Avenge My Village'# (at #C_[35,27]# in the Barrens).", x = 35, y = 27, zone = "The Barrens" },
			[3] = { str = "Go do: #Q_'Avenge My Village'# then turn it in." },
			[4] = { str = "Then do: #Q_'Kill Grundig Darkcloud'# (he is at #C_[73,86]#).", x = 73, y = 86, zone = "Stonetalon Mountains" },
			[5] = { str = "NOTE: I usually skip the escort quest there (#Q_'Protect Kaya'# #C_[73,85]# in the hut), This quest can be a little tough, kill the Sorcerer first that pops out during the one battle you have to fight. If it's too hard for you, you can skip it.", x = 73, y = 85, zone = "Stonetalon Mountains" },
			[6] = { str = "Turn in #Q_'Kill Grundig Darkcloud'# and #Q_'Protect Kaya'# if you did it (at #C_[35,28]# in the Barrens). Accept #Q_'Kaya's Alive'#.", x = 35, y = 28, zone = "The Barrens" },
			[7] = { str = "Then go back to the Barrens." },
		}
	},

-----------16-20 Barrens PART1
	[1618] = {
		title = "16-20 Barrens (part 1)",
		items = {
			[1] = { str = "16-20 Barrens Part 1" },
			[2] = { str = "Grind your way back to the guy in the hut at #C_[45,28]#.", x = 45, y = 28, zone = "The Barrens" },
			[3] = { str = "Turn in #Q_'Kolkar Leaders'#. Accept #Q_'Verog the Dervish'#. Also turn in #Q_'Centaur Bracers'# if it is done, if not don't worry." },
			[4] = { str = "Make sure you finish up #Q_'Raptor Thieves'# #Q_'The Zhevra'# #Q_'Fungal Spores'# and #Q_'The Forgotten Pools'# before returning to the #A_Crossroads# (#A_The Barrens#)." },
			[5] = { str = "Run to #A_Crossroads# (#A_The Barrens#) at #C_[52,30]#.", x = 52, y = 30, zone = "The Barrens" },
			[6] = { str = "Turn in ALL quests, grab ALL new ones." },
			[7] = { str = "Then do #Q_'Apothecary Zamah'# Which has you run all the way to #A_Thunder Bluff# at the spirt rise cave. Do these along the way:" },
			[8] = { str = "Grind your way down south (to #C_[49,50]#) and do #Q_'Lost in Battle'# (Manrik's Wife) She's laying dead by the hut, west of the bridge.", x = 49, y = 50, zone = "The Barrens" },
			[9] = { str = "Go down to #A_Camp Taurajo# (CT) accept #Q_'Tribes at War'# (gnoll in the cage at #C_[44,59]#). Unlock the flight path by talking to the #N_Flight Master#.", x = 44, y = 59, zone = "The Barrens" },
			[10] = { str = "Then do #Q_'Apothecary Zamah'# Which has you run all the way to #A_Thunder Bluff# (in Mulgore at #C_[39,27]#).", x = 39, y = 27, zone = "Mulgore" },

			[11] = { str = "Talk to the #N_Weapon Master# in #A_Thunder Bluff# to learn additional weapon skills at #C_[40,62]#, #HUNTERget Guns and Staff skills. (I do this as a Hunter),# get what weapon skills you need for your class)." },
			[12] = { str = "Talk to your #N_Class Trainer# to learn new spells and abilities." },
			[13] = { str = "Turn in #Q_'Apothecary Zamah'# the cave below the spirit rise at #C_[29,29]#.", x = 29, y = 29, zone = "The Barrens" },
			[14] = { str = "Go to first aid guy at spirit rise to build up first aid." },
			[15] = { str = "Go up to the tower in middle of town. Unlock the flight path by talking to the #N_Flight Master# but DON'T fly back to #A_Crossroads# (#A_The Barrens#)." },
			[16] = { str = "Use your #I_[Hearthstone]# to return to back to #A_Crossroads# (#A_The Barrens#)." },
			[17] = { str = "Turn in #Q_'Lost in Battle'#." },
			[18] = { str = "Then go north west of #A_Crossroads# (#A_The Barrens#) and do #Q_'Prowlers of the Barrens'# #C_[37,20]#.", x = 37, y = 20, zone = "The Barrens" },
			[19] = { str = "Then #Q_'Harpy Lieutenants'# #C_[38,14]#.", x = 38, y = 14, zone = "The Barrens" },
			[20] = { str = "Then grind your way east and do #Q_'Samophlange'# #C_[52,11]# complete the whole chain, which in the end has you kill the goblin for the key." },

			[21] = { str = "Then go east to Sludge Fen and do #Q_'Ignition'# #C_[56,8]#.", x = 56, y = 8, zone = "The Barrens" },
			[22] = { str = "Then do #Q_'The Escape'# (#VIDEOSee video on how I do Ignition and Escape#)." },
			[23] = { str = "Then go north-east and do #Q_'Miner's Fortune'# #C_[61,5]#.", x = 61, y = 5, zone = "The Barrens" },
			[24] = { str = "Then grind your way south to #A_Ratchet# (#A_The Barrens#) #C_[63,37]#.", x = 63, y = 37, zone = "The Barrens" },
			[25] = { str = "Turn in #Q_'Stolen Booty'#  #Q_'Samophlange'# #Q_'The Escape'#. Accept #Q_'Ziz Fizziks'# and SKIP '#N_Wenikee Boltbucket'#." },
			[26] = { str = "Turn in #Q_'Miner's Fortune'#." },
			[27] = { str = "Then go west of #A_Ratchet# (#A_The Barrens#) and do #Q_'The Stagnant Oasis'# and #Q_'Verog the Dervish'# at #C_[54,43]#.", x = 54, y = 43, zone = "The Barrens" },
			[28] = { str = "Then run to #A_Crossroads# (#A_The Barrens#) #C_[52,30]#.", x = 52, y = 30, zone = "The Barrens" },
			[29] = { str = "Turn in #Q_'Prowlers of the Barrens'# #Q_'Harpy Lieutenants'# and #Q_'The Stagnant Oasis'#. Accept #Q_'Altered Beings'# #Q_'Echeyakee'# #Q_'Serena Bloodfeather'# #Q_'Report to Kadrak'# and #Q_'Egg Hunt'#." },
			[30] = { str = "Go west of #A_Crossroads# (#A_The Barrens#) #C_[45,28]#, to turn in: #Q_'Centaur Bracers'# and #Q_'Verog the Dervish'#. I SKIP '#N_Hezrul Bloodmark'#.", x = 45, y = 28, zone = "The Barrens" },

			[31] = { str = "Then grind your way north and do: #Q_'Serena Bloodfeather'# at #C_[38,11]#.", x = 38, y = 11, zone = "The Barrens" },
			[32] = { str = "Then go east (grind mobs along the way) and do #Q_'Echeyakee'# (coords are at #C_[55,17]#).", x = 55, y = 17, zone = "The Barrens" },
			[33] = { str = "Use your #I_[Hearthstone]# to return to #A_Crossroads# (#A_The Barrens#)." },
			[34] = { str = "Turn in #Q_'Echeyakee'#. Accept #Q_'The Angry Scytheclaws'#." },
			[35] = { str = "Turn in #Q_'Serena Bloodfeather'#. Accept #Q_'Letter to Jin'Zil'#. Make sure that #Q_'Consumed by Hatred'# is also accepted." },
		}
	},

-----------16-20 Barrens PART2
	[1820] = {
		title = "16-20 Barrens (part 2)",
		items = {
			[1] = { str = "16-20 Barrens Part 2" },
			[2] = { str = "Go down south and do:" },
			[3] = { str = "#Q_'Altered Beings'# #C_[55,42]#.", x = 55, y = 42, zone = "The Barrens" },
			[4] = { str = "#Q_'Raptor Horns'# along with #Q_'Stolen Silver'# at #C_[57,54]#.", x = 57, y = 54, zone = "The Barrens" },
			[5] = { str = "#Q_'The Angry Scytheclaws'# #C_[51,46]#.", x = 51, y = 46, zone = "The Barrens" },
			[6] = { str = "#Q_'Tribes at War'# along with #Q_'Consumed by Hatred'# at #C_[51,54]#.", x = 51, y = 54, zone = "The Barrens" },
			[7] = { str = "Then grind your way to #A_Camp Taurajo# #C_[45,58]#.", x = 45, y = 58, zone = "The Barrens" },
			[8] = { str = "Accept #Q_'Weapons of Choice'#." },
			[9] = { str = "Go to gnoll in the cage." },
			[10] = { str = "Turn in #Q_'Tribes at War'#. Accept #Q_'Blood Shards of Agamaggan'# and #Q_'Betrayal from Within'#." },

			[11] = { str = "Turn in #Q_'Blood Shards of Agamaggan'#." },
			[12] = { str = "Turn in 10 bloodshards for #Q_'Spirit of the Wind'# (repeatable quest) for fast run speed." },
			[13] = { str = "At this point, you should be about half way to level 20." },
			[14] = { str = "Now comes the fun part: #N_Wailing Caverns# Instance! at #C_[46,36]#. You will need a full group to do this. OPTIONAL: GRIND all the way to level 20! (which is what I do, but it's not recommended). I grind away at beasts and brisstlebacks just north of CT." },
			[15] = { str = "Keep an eye out for the mobs named #N_Lakota'mani# (a big grey kodo around #C_[45,53]#) and #N_Owatanka#. They drops #N_Hoof of Lakota'mani# which starts #Q_'Lakota'mani'# and #N_Owatanka's Tailspike# which starts #Q_'Owatanka'#. Turn these in at CT when you get the chance." },
			[16] = { str = "Once you are at least 5 bars away from 20, run to #A_Crossroads# (#A_The Barrens#)." },
			[17] = { str = "Turn in #Q_'Stolen Silver'# #Q_'Consumed by Hatred'# #Q_'Altered Beings'# and #Q_'The Angry Scytheclaws'#.accept all new quests. SKIP '#N_Mura Runetotem'#. You should be lvl 20 now." },
			[18] = { str = "Fly to #A_Orgrimmar# and talk to your #N_Class Trainer# to learn new spells and abilities." },
			[19] = { str = "Grab #Q_'The Ashenvale Hunt'# quest." },
			[20] = { str = "Use your #I_[Hearthstone]# to return to back to #A_Crossroads# (#A_The Barrens#)." },

			[21] = { str = "Fly to #A_Ratchet# (#A_The Barrens#)." },
			[22] = { str = "Turn in #Q_'Chen's Empty Keg'# (if you did it, if not don't worry). Accept the next part to it." },
			[23] = { str = "Turn in #Q_'Raptor Horns'#. Accept #Q_'Deepmoss Spider Eggs'# and #Q_'Ziz Fizziks'#." },
			[24] = { str = "Then go do: #Q_'The Guns of Northwatch'# at #C_[60,55]# (#VIDEOSee complete video on this quest along with the escort quest#).", x = 60, y = 55, zone = "The Barrens" },
			[25] = { str = "Then once #Q_'The Guns of Northwatch'# is done, do:" },
			[26] = { str = "#Q_'Free From the Hold'# (escort quest)." },
			[27] = { str = "Turn in both quests at #A_Ratchet# (#A_The Barrens#), then." },
			[28] = { str = "Use your #I_[Hearthstone]# to return to #A_Crossroads# (#A_The Barrens#). (if hearth stone is still on a cooldown, fly to #A_Crossroads# (#A_The Barrens#))." },
			[29] = { str = "Run west into #A_Stonetalon Mountains#." },
		}
	},
}
